(46, 1, '2023-04-27 11:47:45', '2023-04-27 11:47:45', '', 'opera-chrome-safari-firefox', '', 'inherit', 'open', 'closed', '', 'opera-chrome-safari-firefox', '', '', '2023-04-27 11:47:45', '2023-04-27 11:47:45', '', 36, 'http://dxdigitals.hstn.me/wp-content/uploads/2023/04/opera-chrome-safari-firefox.jpg', 0, 'attachment', 'image/jpeg', 0),
(47, 1, '2023-04-27 11:48:19', '2023-04-27 11:48:19', '', 'disk-c-malo-mesta', '', 'inherit', 'open', 'closed', '', 'disk-c-malo-mesta', '', '', '2023-04-27 11:48:19', '2023-04-27 11:48:19', '', 36, 'http://dxdigitals.hstn.me/wp-content/uploads/2023/04/disk-c-malo-mesta.jpg', 0, 'attachment', 'image/jpeg', 0),
