(22, 1, '2023-05-13 10:57:12', '2023-05-13 10:57:12', '<!-- wp:paragraph -->\n<p>// ComponiumView.h : interface of the CComponiumView class<br>//</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">pragma once</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"COMPO/UTLCOMPO.H\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>class CComponiumDoc;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>class CComponiumView : public CView<br>{<br>enum {NOff=3}; /* pocet vrstev ve stope (horni, osnova, dolni) */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>struct OffInfo<br>{<br>int xpos; // last unit pos<br>int zpos;<br>CasT zcas; // last unit time<br>CasT xcas;<br>bool synchro;<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum tramsmer {TJedno,TDolu,TNahoru,TRadDolu,TRadNahoru} ; // note leg direction</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>struct GrfStatus<br>{<br>OffInfo i[NOff];<br>CasT xcasspol; /* nejvyssi ze vsech kanalu */<br>Err stat;<br>int PredTon; // key before this unit<br>int ZBlok,KBlok; // selection graphical coordinates<br>byte PoslBlok;<br>PosT RelPos; // number of items rendered so far - offset<br>bool PridanTram; // beam flag<br>int TramX,TramY; // beam coordinates, &lt;0 negative means beam<br>int NTram; // how many bars<br>tramsmer TramSmer;<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum {MaxVec=16};<br>struct VNota<br>{<br>JednotkaBuf t; /* vsechny param. */<br>WindRect *W; /* kde se nakreslily <em>/ Flag JeKursor; /</em> zda na ni je kursor */<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>struct Vector /* buffer na vektory not <em>/ { VNota v[MaxVec]; int n; /</em> pocet not ve vektoru */<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>struct OknoInfo<br>{<br>SekvSoubor Soub[NMaxHlasu]; /* nastaven na zacatek okna <em>/ /</em> ze Soub[0] lze cist hlavni sekvenci! <em>/ //int NSoub; /</em> pocet zobraz. hlasu <em>/ int KKan; /</em> hlas, ve kterem je kursor <em>/ //SekvSoubor Kurs; /</em> pozice kursoru <em>/ WindRect KR; /</em> pozice kurzoru (rel. v okne ) <em>/ //CasT ZacCas,KonCas; /</em> historie - kazdy vznika jinak <em>/ //CasT CasZac,CasKon; int IStopa,NStop; /</em> prvni zobr. hlas, pocet zobr. hlasu <em>/ int ZBStopa,KBStopa; /</em> prvni hlas v bloku, posl hl. v bl. (&lt;0 = odp. soub. uzavren) <em>/ SekvSoubor ZBlok,KBlok; /</em> zac. bloku v 1. hlase, konec v posl. hlase <em>/ //Flag ZmenaObrazu; /</em> nucene prekreslovani celeho pri lib. casti <em>/ //Flag ZobrazKursor; /</em> vynucene zobrazeni kursoru <em>/ //Flag VNazvuZmena; //Flag Rozvoj; /</em> indikace - tento rezim by mel byt ve vsech souborech */<br>} OI;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vector TonVec[NMaxStop];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>GrfStatus KIStat[NMaxStop];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>protected: // create from serialization only<br>CComponiumView();<br>DECLARE_DYNCREATE(CComponiumView)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// Attributes<br>public:<br>CComponiumDoc* GetDocument() const;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// Operations<br>public:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// Overrides<br>public:<br>virtual BOOL PreCreateWindow(CREATESTRUCT&amp; cs);<br>virtual void OnDraw(CDC* pDC) override;<br>protected:<br>virtual void OnInitialUpdate(); // called first time after construct</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// Implementation<br>public:<br>virtual ~CComponiumView();</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">ifdef _DEBUG</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>virtual void AssertValid() const;<br>virtual void Dump(CDumpContext&amp; dc) const;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>protected:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void FKresliNoty(CDC *dc);<br>void ZacniKresl(SekvSoubor *ss);<br>void KresliJedn( SekvSoubor *s, int xpos, int Kan, int offs );<br>void KresliTramH( int x1, int y1, int x2, int y2, int Cet );<br>void KresliTramD( int x1, int y1, int x2, int y2, int Cet );</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>bool JeVBloku( int Kan, const StrPosT *pos, CasT cas );<br>void UzavriTram( GrfStatus *KS );<br>void ZavedTram( GrfStatus *KS, int x, int y, int Cet, enum tramsmer Smer );<br>int FKresliTonD( GrfStatus *KS, WindRect *W, JednItem *B, int xpos, int ypos, Flag Prapor, Flag Musi );<br>int FKresliTonH( GrfStatus *KS, WindRect *W, JednItem *B, int xpos, int ypos, Flag Prapor, Flag Musi );<br>int FKresliTon( GrfStatus *KS, WindRect *W, JednItem *B, int xpos, int ypos );<br>int FKresliPau( WindRect *W, JednItem *B, int xpos, int ypos, int off );<br>int KresliVec( GrfStatus *KS, Vector *V, int xpos, int ypos);<br>int KresliTon( GrfStatus *KS, WindRect *W, SekvSoubor *s, int xpos, int ypos, int Kan );</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void SetridVec( Vector *V );</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// Generated message map functions<br>protected:<br>afx_msg void OnStyleChanged(int nStyleType, LPSTYLESTRUCT lpStyleStruct);<br>afx_msg void OnFilePrintPreview();<br>afx_msg void OnRButtonUp(UINT nFlags, CPoint point);<br>afx_msg void OnContextMenu(CWnd* pWnd, CPoint point);<br>DECLARE_MESSAGE_MAP()<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">ifndef _DEBUG // debug version in ComponiumView.cpp</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>inline CComponiumDoc* CComponiumView::GetDocument() const<br>{ return reinterpret_cast(m_pDocument); }</p>\n<!-- /wp:paragraph --