(25, 1, '2023-05-13 10:58:22', '2023-05-13 10:58:22', '<!-- wp:paragraph -->\n<p>// MainFrm.cpp : implementation of the CMainFrame class<br>//</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"stdafx.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"Componium.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"MainFrm.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">ifdef _DEBUG</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define new DEBUG_NEW</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>// CMainFrame</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>IMPLEMENT_DYNAMIC(CMainFrame, CMDIFrameWndEx)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const int iMaxUserToolbars = 10;<br>const UINT uiFirstUserToolBarId = AFX_IDW_CONTROLBAR_FIRST + 40;<br>const UINT uiLastUserToolBarId = uiFirstUserToolBarId + iMaxUserToolbars - 1;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>BEGIN_MESSAGE_MAP(CMainFrame, CMDIFrameWndEx)<br>ON_WM_CREATE()<br>ON_COMMAND(ID_WINDOW_MANAGER, &amp;CMainFrame::OnWindowManager)<br>ON_COMMAND(ID_VIEW_CUSTOMIZE, &amp;CMainFrame::OnViewCustomize)<br>ON_REGISTERED_MESSAGE(AFX_WM_CREATETOOLBAR, &amp;CMainFrame::OnToolbarCreateNew)<br>ON_COMMAND_RANGE(ID_VIEW_APPLOOK_WIN_2000, ID_VIEW_APPLOOK_WINDOWS_7, &amp;CMainFrame::OnApplicationLook)<br>ON_UPDATE_COMMAND_UI_RANGE(ID_VIEW_APPLOOK_WIN_2000, ID_VIEW_APPLOOK_WINDOWS_7, &amp;CMainFrame::OnUpdateApplicationLook)<br>END_MESSAGE_MAP()</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static UINT indicators[] =<br>{<br>ID_SEPARATOR, // status line indicator<br>ID_INDICATOR_CAPS,<br>ID_INDICATOR_NUM,<br>ID_INDICATOR_SCRL,<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// CMainFrame construction/destruction</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CMainFrame::CMainFrame()<br>{<br>// TODO: add member initialization code here<br>theApp.m_nAppLook = theApp.GetInt(_T(\"ApplicationLook\"), ID_VIEW_APPLOOK_WINDOWS_7);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CMainFrame::~CMainFrame()<br>{<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int CMainFrame::OnCreate(LPCREATESTRUCT lpCreateStruct)<br>{<br>if (CMDIFrameWndEx::OnCreate(lpCreateStruct) == -1)<br>return -1;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>BOOL bNameValid;\n\nCMDITabInfo mdiTabParams;\nmdiTabParams.m_style = CMFCTabCtrl::STYLE_3D_ONENOTE; // other styles available...\nmdiTabParams.m_bActiveTabCloseButton = TRUE;      // set to FALSE to place close button at right of tab area\nmdiTabParams.m_bTabIcons = FALSE;    // set to TRUE to enable document icons on MDI taba\nmdiTabParams.m_bAutoColor = TRUE;    // set to FALSE to disable auto-coloring of MDI tabs\nmdiTabParams.m_bDocumentMenu = TRUE; // enable the document menu at the right edge of the tab area\nEnableMDITabbedGroups(TRUE, mdiTabParams);\n\nif (!m_wndMenuBar.Create(this))\n{\n    TRACE0(\"Failed to create menubar\\n\");\n    return -1;      // fail to create\n}\n\nm_wndMenuBar.SetPaneStyle(m_wndMenuBar.GetPaneStyle() | CBRS_SIZE_DYNAMIC | CBRS_TOOLTIPS | CBRS_FLYBY);\n\n// prevent the menu bar from taking the focus on activation\nCMFCPopupMenu::SetForceMenuFocus(FALSE);\n\nif (!m_wndToolBar.CreateEx(this, TBSTYLE_FLAT, WS_CHILD | WS_VISIBLE | CBRS_TOP | CBRS_GRIPPER | CBRS_TOOLTIPS | CBRS_FLYBY | CBRS_SIZE_DYNAMIC) ||\n    !m_wndToolBar.LoadToolBar(theApp.m_bHiColorIcons ? IDR_MAINFRAME_256 : IDR_MAINFRAME))\n{\n    TRACE0(\"Failed to create toolbar\\n\");\n    return -1;      // fail to create\n}\n\nCString strToolBarName;\nbNameValid = strToolBarName.LoadString(IDS_TOOLBAR_STANDARD);\nASSERT(bNameValid);\nm_wndToolBar.SetWindowText(strToolBarName);\n\nCString strCustomize;\nbNameValid = strCustomize.LoadString(IDS_TOOLBAR_CUSTOMIZE);\nASSERT(bNameValid);\nm_wndToolBar.EnableCustomizeButton(TRUE, ID_VIEW_CUSTOMIZE, strCustomize);\n\n// Allow user-defined toolbars operations:\nInitUserToolbars(NULL, uiFirstUserToolBarId, uiLastUserToolBarId);\n\nif (!m_wndStatusBar.Create(this))\n{\n    TRACE0(\"Failed to create status bar\\n\");\n    return -1;      // fail to create\n}\nm_wndStatusBar.SetIndicators(indicators, sizeof(indicators)/sizeof(UINT));\n\n// TODO: Delete these five lines if you don\'t want the toolbar and menubar to be dockable\nm_wndMenuBar.EnableDocking(CBRS_ALIGN_ANY);\nm_wndToolBar.EnableDocking(CBRS_ALIGN_ANY);\nEnableDocking(CBRS_ALIGN_ANY);\nDockPane(&amp;m_wndMenuBar);\nDockPane(&amp;m_wndToolBar);\n\n\n// enable Visual Studio 2005 style docking window behavior\nCDockingManager::SetDockingMode(DT_SMART);\n// enable Visual Studio 2005 style docking window auto-hide behavior\nEnableAutoHidePanes(CBRS_ALIGN_ANY);\n// set the visual manager and style based on persisted value\nOnApplicationLook(theApp.m_nAppLook);\n\n// Enable enhanced windows management dialog\nEnableWindowsDialog(ID_WINDOW_MANAGER, ID_WINDOW_MANAGER, TRUE);\n\n// Enable toolbar and docking window menu replacement\nEnablePaneMenu(TRUE, ID_VIEW_CUSTOMIZE, strCustomize, ID_VIEW_TOOLBAR);\n\n// enable quick (Alt+drag) toolbar customization\nCMFCToolBar::EnableQuickCustomization();\n\nif (CMFCToolBar::GetUserImages() == NULL)\n{\n    // load user-defined toolbar images\n    if (m_UserImages.Load(_T(\".\\\\UserImages.bmp\")))\n    {\n        CMFCToolBar::SetUserImages(&amp;m_UserImages);\n    }\n}\n\n// enable menu personalization (most-recently used commands)\n// TODO: define your own basic commands, ensuring that each pulldown menu has at least one basic command.\nCList&lt;UINT, UINT&gt; lstBasicCommands;\n\nlstBasicC