ar *Nam ){return Nam;}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/*<br>*/</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>NastrDef *NastrAcc( DynPole *P, int NN )<br>{<br>if( NN&amp;0x4000 )<br>{<br>Plati( P );<br>return AccDyn(P,NN&amp;0x3fff);<br>}<br>return AccDyn(&amp;NDef,NN);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int NajdiOptSoub( NastrDef <em>D, int Ton ) { int ni; int mindif=FyzOkt</em>12*2,dif;<br>int mini=-1;<br>Flag FF=False;<br>for( ni=1; niSoubory[ni];<br>if( *N ) FF=True;<br>}<br>if( !FF ) return 0;<br>for( ni=0; niSoubory[ni];<br>if( *N )<br>{<br>NastrSoub *NS;<br>long S=ZavedSoub(D-&gt;Soubory[ni],2);<br>if( S&lt;0 ) return -1; NS=AccDyn(&amp;NSou,S); dif=abs(Ton-NS-&gt;BasNota);<br>if( dif&lt;mindif ) mindif=dif,mini=ni;<br>}<br>}<br>return mini;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int DefHNastr( const char *NNam, int Ton, int Tip, Pisen *P )<br>{<br>int I=NajdiDef(NNam,Tip,&amp;P-&gt;LocDigi);<br>if( I&gt;=EOK )<br>{<br>NastrDef <em>D=NastrAcc(&amp;P-&gt;LocDigi,I); long S; int FTon=F_FyzTon(Ton); int ni=NajdiOptSoub(D,Ton); if( ni&lt;0 ) return KON; S=ZavedSoub(D-&gt;Soubory[ni],2); if( S==ERR ) /</em>AlertRF(CENASFA,1,NajdiNazev(D-&gt;Soubory[ni])),<em>/ S=ECAN; if( SIMSoubory[FTon]=S; D-&gt;Uzit=True; /</em> potrebujeme vedet, koho mame ukladat do COC */<br>}<br>else return KON;<br>return I;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">if 0</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>int DefHNastr( const char *NNam, int Tip, int *KL, int *KH, Flag *Rytm, Pisen *P )<br>{<br>int I=NajdiDef(NNam,Tip,&amp;P-&gt;LocDigi);<br>if( I&gt;=EOK )<br>{<br>NastrDef *D=NastrAcc(&amp;P-&gt;LocDigi,I);<br>long S;<br>if( AktHraj==&amp;AYBufFalc ) S=ZavedSoub(D-&gt;Soubor,2);<br>else S=ZavedSoub(D-&gt;Soubor,1);<br>if( S==ERR ) S=ECAN;<br>if( SISoub=S;<br>}<br>else return KON;<br><em>KL=0,</em>KH=0x7fff; /* bereme jakykoliv kanal */<br>*Rytm=False; /* rytmika taky zabira cas */<br>return I;<br>return 0;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>/* ---------------- editace nastroje */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* LLHRAJ zapouzdreni sluzeb */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* uroven orchestru - NastrDef */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const char *EditSNastr( const char *NNam, const WindRect *Orig, Pisen *P )<br>{<br>(void)Orig;<br>(void)P;<br>return NNam;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void MazSNastr( void )<br>{<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* uroven orchestru */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const char *NastrInfo( const char *NNam, Pisen *P )<br>{<br>(void)P;<br>return NNam;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err SaveHNastr( FILE *f, Pisen *P, const char <em>A ) /</em> uloz vsechny pouzite nastroje */<br>{<br>(void)f,(void)A;<br>(void)P;<br>return EFIL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void AktHNastr( void )<br>{<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err VymazNastr( char *N, Pisen *P )<br>{<br>(void)N,(void)P;<br>return EFIL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err KonSNastr( void )<br>{<br>return EFIL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err CtiOrchestr( void )<br>{<br>return EFIL;<br>}<br>Err PisOrchestr( void )<br>{<br>return EFIL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const char **SoupisSNastr( Pisen *P )<br>{<br>(void)P;<br>return NULL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* sekce DSP */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int CAb,AbVal=-1;</p>\n<!-- /wp:paragraph -->\n\n<!