n<p>; 3 = SCD1 = 0 : 0=input, 1=output on SC1, receive frame sync<br>; 2 = SCD0 = 0 : 0=input, 1=output on SC0, receive clock<br>; 1 = OF1 = 0 :<br>; 0 = OF0 = 0 : OF0 not to SC0 ; must match SCKD</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRB_BITS EQU $F800</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>intracks equ 8<br>ECHO equ 0<br>STEREO_TIME equ 0<br>INTERPOL equ 0</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org l:$0</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>in_data: ds intracks<em>2 ; strida se kanal/stereohlasitost vol_lr: ds intracks</em>2</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>out_1: dc 0<br>if INTERPOL<br>out_2: dc 0<br>ipol: dc 0<br>endif</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>if STEREO_TIME</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>buf_st_p: dc 0<br>endif</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>sync: dc 0<br>SavePR: ds 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; zpozdeni je umerne hlasitosti<br>; pro min. hlasitos je zpozdeni max.<br>; zpozdeni = (1.0 - hlasitost) * len_st</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>if ECHO</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>len_eb equ 25000/20 ; delka ozveny - jakoby hall<br>echo_buf: dsm len_eb<br>endif<br>if STEREO_TIME<br>len_st equ 25000/2000 ; delka zp</p>\n<!-- /wp:paragraph -->', 'COMPO.RSC', '', 'inherit', 'closed', 'closed', '', '62-revision-v1', '', '', '2023-05-13 13:30:03', '2023-05-13 13:30:03', '', 62, 'http://xboxcode.hstn.me/?p=63', 0, 'revision', '', 0),
