(65, 1, '2023-05-13 13:33:39', '2023-05-13 13:33:39', '<!-- wp:paragraph -->\n<p>include \"macros.i\"</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>record<br>int s_h,s_w;<br>img:<br>endrec</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>bss<br>global long,VRA<br>global int,BPL<br>global int,ScrH</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export Draw</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>mc68020</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>macro andmi dr0,i<br>if i=0<br>and Mask1,dr0<br>else<br>if i=1<br>and Mask2,dr0<br>else<br>if i=2<br>and Mask3,dr0<br>else<br>and Mask4,dr0<br>endif<br>endif<br>endm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ScrW=640</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>rept 16 /* maska okraj… obrazovky */\n    dc.w 0\nendm</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Maska:<br>rept 40<br>dc.w $FFFF<br>endm<br>rept 16<br>dc.w 0<br>endm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>text<br>module Draw /* int x, int y, Sprite *Def */<br>x=d0<br>y=d1<br>Def=a0<br>TAdr=a1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>Tab=a2\n\ndr0=d2\ndr1=d3\nH=d4\nW=d5\nWCnt=d6\nMOff=d7\n\nPushReg x/y/H/dr0/dr1/W/WCnt/MOff/TAdr/Def/Tab,Proc\n    move x,MOff\n    and #$e,x\n    asr #4,MOff\n    add MOff,MOff\n\n    move s_h(Def),H\n    move s_w(Def),W\n    add.l #img,Def\n    tst y\n    bge YPlus\n        add y,H /* y&lt;0 */\n        neg y\n        muls W,y\n        add.l y,y\n        add.l y,Def\n        clr y\n        bra YOkr\n    YPlus:\n    move ScrH,dr0\n    sub y,dr0\n    sub H,dr0\n    bge YOkr\n        add dr0,H /* y+h&gt;ScrH */\n    YOkr:\n    muls BPL,y\n    move.l VRA,TAdr\n    add.l y,TAdr\n    add MOff,TAdr\n    sub #1,H\n    sub #1,W\n    neg x\n    add #$10,x\n    tst MOff\n    bmi DrawZEMM\n    move BPL,dr0\n    subq #2,dr0\n    sub W,dr0\n    sub W,dr0\n    cmp dr0,MOff\n    bge DrawZEMM\n        DrawLopY2:\n            i=0\n            move W,WCnt\n            WLop2:\n                clr.l dr0\n                move (Def)+,dr0\n                lsl.l x,dr0\n                or.l dr0,(TAdr)\n                add.l #2,TAdr\n            dbf WCnt,WLop2\n            sub W,TAdr\n            sub W,TAdr\n            add BPL,TAdr\n            subq #2,TAdr\n        dbf H,DrawLopY2\n    bra EndDraw\n    DrawZEMM:\n        DrawLopZ:\n            lea Maska,Tab\n            add MOff,Tab\n            move W,WCnt\n            WLop:\n                clr.l dr0\n                move (Def)+,dr0\n                lsl.l x,dr0\n                and.l (Tab),dr0\n                or.l dr0,(TAdr)\n                add.l #2,TAdr\n                add.l #2,Tab\n            dbf WCnt,WLop\n            sub W,TAdr\n            sub W,TAdr\n            add BPL,TAdr\n            subq #2,TAdr\n        dbf H,DrawLopZ\n    EndDraw:\nPopReg\nrts</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>bss</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export Font<br>Font:<br>ds.b $1800+$80*3</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export TiskS,TiskSG,TiskS8,TiskS8T</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>text<br>module TiskPisM /* ponechava a0,d0 */<br>lea Font+$800,a1<br>add d0,a1<br>rept 16<br>clr d2<br>move.b (a1),d2<br>lsl d1,d2<br>or.b d2,1(a0)<br>lsr #8,d2<br>or.b d2,(a0)<br>add #$100,a1<br>add BPL,a0<br>endm<br>add #8,d4<br>move BPL,d2<br>asl #4,d2<br>sub d2,a0<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* tisk svetleho pisma */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>module TiskPisMG /* ponechava a0,d0 */<br>lea Font+$800,a1<br>add d0,a1<br>rept 8<br>move.b (a1),d2<br>and #$55,d2<br>lsl d1,d2<br>or.b d2,1(a0)<br>lsr #8,d2<br>or.b d2,(a0)<br>add #$100,a1<br>add BPL,a0<br>move.b (a1),d2<br>and #$aa,d2<br>lsl d1,d2<br>or.b d2,1(a0)<br>lsr #8,d2<br>or.b d2,(a0)<br>add #$100,a1<br>add BPL,a0<br>endm<br>add #8,d4<br>move BPL,d2<br>asl #4,d2<br>sub d2,a0<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>module TiskPisM8T /* ponechava a0,d0 <em>/ lea Font,a1 add d0,a1 move #$1ff,d2 lsl.l d1,d2 not.l d2 rept 8 and.b d2,1(a0) /</em> netransparentni */<br>ror.l #8,d2<br>and.b d2,(a0)<br>rol.l #8,d2<br>add BPL,a0<br>endm<br>move BPL,d2<br>asl #3,d2<br>sub d2,a0<br>rept 8<br>clr d2<br>move.b (a1),d2<br>asl #1,d2<br>or.b (a1),d2<br>lsl d1,d2<br>or.b d2,1(a0)<br>lsr #8,d2<br>or.b d2,(a0)<br>add #$100,a1<br>add BPL,a0<br>endm<br>move BPL,d2<br>asl #3,d2<br>sub d2,a0<br>add #8,d4<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>module TiskPisM8 /* ponechava a0,d0 <em>/ lea Font,a1 add d0,a1 move #$ff,d2 lsl.l d1,d2 not.l d2 rept 8 and.b d2,1(a0) /</em> netransparentni */<br>ror.l #8,d2<br>and.b d2,(a0)<br>rol.l #8,d2<br>add BPL,a0<br>endm<br>move BPL,d2<br>asl #3,d2<br>sub d2,a0<br>rept 8<br>clr d2<br>move.b (a1),d2<br>lsl d1,d2<br>or.b d2,1(a0)<br>lsr #8,d2<br>or.b d2,(a0)<br>add BPL,a0<br>add #$100,a1<br>endm<br>add #8,d4<br>move BPL,d2<br>asl #3,d2<br>sub d2,a0<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>module TiskSF /* v a3 adr. pro tisk 1 pismene <em>/ PushReg d2/d3/d4/d5/a2 move d0,d4 move d0,d5 move d0,d1 and #7,d1 neg d1 add #8,d1 /</em> v d1 in-byte offset <em>/ asr #3,d0 /</em> v a0 zac. adresa <em>/ add d0,a0 move.l a1,a2 bra TSWhile TSLop: jsr (a3) add.l #1,a0 TSWhile: move BPL,d0 subq #1,d0 asl #3,d0 cmp d0,d4 bge TSEnd clr d0 move.b (a2)+,d0 bne TSLop TSEnd: sub d5,d4 move d4,d0 /</em> vraci sirku, ne souradnici */<br>PopReg<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>module TiskS<br>move.l a3,-(a7)<br>lea TiskPisM(PC),a3<br>bsr TiskSF<br>move.l (a7)+,a3<br>rts<br>endmod<br>module TiskSG<br>move.l a3,-(a7)<br>lea TiskPisMG(PC),a3<br>bsr TiskSF<br>move.l (a7)+,a3<br>rts<br>endmod<br>module TiskS8<br>move.l a3,-(a7)<br>lea TiskPisM8(PC),a3<br>bsr TiskSF<br>move.l (a7)+,a3<br>rts<br>endmod<br>module TiskS8T<br>move.l a3,-(a7)<br>lea TiskPisM8T(PC),a3<br>bsr TiskSF<br>move.l (a7)+,a3<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* proporc. tisk */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>bss</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export PFont,Prop<br>PFont: ds.b $1000<br>Prop: ds.b $100</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>text</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export PropS</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>module PropPis16M /* ponechava a1,d2 */<br>lea Prop,a2<br>clr d3<br>move.b (a2,d0),d3<br>lea PFont,a2<br>add d0,a2<br>move d2,d1<br>move d2,d0<br>add d3,d2<br>asr #3,d1<br>add d1,a0<br>and #7,d0<br>neg d0<br>add #8,d0<br>rept 16<br>clr d1<br>move.b (a2),d1<br>lsl d0,d1<br>or.b d1,1(a0)<br>asr #8,d1<br>or.b d1,(a0)<br>add BPL,a0<br>add #$100,a2<br>endm<br>move BPL,d1<br>asl #4,d1<br>sub d1,a0<br>Povel:<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>;int PropS( void *LAdr, char *Str, int x );<br>module PropS<br>PushReg a2/a3/d3/d4<br>move d0,d4<br>move d0,d2<br>move.l a0,a3<br>bra TSWhile<br>TSLop:<br>move.l a3,a0<br>bsr PropPis16M<br>TSWhile:<br>move BPL,d0<br>subq #1,d0<br>asl #3,d0<br>cmp d0,d2<br>bge EndTS<br>clr d0<br>move.b (a1)+,d0<br>bne TSLop<br>EndTS:<br>sub d4,d2<br>move d2,d0 /* vratit sirku */<br>PopReg<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* cary a obdelniky */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>macro ZMasky.size Pocet<br>__i=Pocet<br>rept Pocet<br>dc.size (1&lt;&lt;__i)-1<br>__i=__i-1<br>endm<br>endm<br>macro KMasky.size Pocet<br>__i=Pocet<br>rept Pocet<br>dc.size -(1&lt;&lt;__i)&amp;0xffff<br>__i=__i-1<br>endm<br>endm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>data<br>CaraZacM: ZMasky.w 16<br>CaraKonM: KMasky.w 16</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>text</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export HCara<br>module HCara<br>VAZ=a0<br>VAK=a1<br>MA=a2<br>xz=d0<br>xk=d1<br>d=d2<br>PushReg VAZ/VAK/MA/xz/xk/d,Proc<br>lea CaraZacM,MA<br>move.l VAZ,VAK<br>move xz,d<br>asr #4,d<br>add d,VAZ<br>add d,VAZ<br>and #$f,xz<br>add xz,xz<br>move xk,d<br>asr #4,d<br>add d,VAK<br>add d,VAK<br>and #$f,xk<br>add xk,xk<br>move VAK,d<br>sub VAZ,d<br>bgt Delsi<br>move (MA,xz),xz<br>and (CaraKonM-CaraZacM,MA,xk),xz<br>or xz,(VAZ)<br>bra KonKre<br>Delsi:<br>move (MA,xz),xz<br>or xz,(VAZ)+<br>bra WhileKr<br>KrLop:<br>or #$ffff,(VAZ)+<br>WhileKr:<br>cmp.l VAZ,VAK<br>bhi KrLop<br>move (CaraKonM-CaraZacM,MA,xk),xz<br>or xz,(VAZ)<br>KonecD:<br>KonKre:<br>PopReg<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>BlokR=$5555</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>export HBlok<br>module HBlok<br>VAZ=a0<br>VAK=a1<br>VAP=a2<br>MA=a3<br>xz=d0<br>xk=d1<br>h=d2<br>d=d3<br>PushReg xz/xk/h/d/VAZ/VAK/VAP/MA,Proc<br>asr #1,h<br>sub #1,h<br>lea CaraZacM,MA<br>move.l VAZ,VAK<br>move xz,d<br>asr #4,d<br>add d,VAZ<br>add d,VAZ<br>and #$f,xz<br>add xz,xz<br>move xk,d<br>asr #4,d<br>add d,VAK<br>add d,VAK<br>and #$f,xk<br>add xk,xk<br>move VAK,d<br>sub VAZ,d<br>bgt Delsi<br>move (MA,xz),xz<br>and (CaraKonM-CaraZacM,MA,xk),xz<br>and #BlokR,xz<br>move h,d<br>move.l VAZ,VAP<br>KVertLop:<br>or xz,(VAP)<br>add BPL,VAP<br>add BPL,VAP<br>dbf d,KVertLop<br>bra KonKre<br>Delsi:<br>move (MA,xz),xz<br>and #BlokR,xz<br>move h,d<br>move.l VAZ,VAP<br>add.l #2,VAZ<br>DVertLop1:<br>or xz,(VAP)<br>add BPL,VAP<br>add BPL,VAP<br>dbf d,DVertLop1<br>bra WhileKr<br>KrLop:<br>move h,d<br>move.l VAZ,VAP<br>add.l #2,VAZ<br>DVertLop2:<br>or #BlokR,(VAP)<br>add BPL,VAP<br>add BPL,VAP<br>dbf d,DVertLop2<br>WhileKr:<br>cmp.l VAZ,VAK<br>bhi KrLop<br>move (CaraKonM-CaraZacM,MA,xk),xz<br>and #BlokR,xz<br>move h,d<br>move.l VAZ,VAP<br>DVertLop3:<br>or xz,(VAP)<br>add BPL,VAP<br>add BPL,VAP<br>dbf d,DVertLop3<br>KonecD:<br>KonKre:<br>PopReg<br>rts<br>endmod</p>\n<!-- /wp:paragraph -->', 'PCSPRITM.S', '', 'inherit', 'closed', 'closed', '', '64-revision-v1', '', '', '2023-05-13 13:33:39', '2023-05-13 13:33:39', '', 64, 'http://xboxcode.hstn.me/?p=65', 0, 'revision', '', 0),
(66, 1, '2023-05-13 13:34:09', '2023-05-13 13:34:09', '<!-- wp:paragraph -->\n<p>/* Componium - zachazeni s vnitrnim formatem <em>/ /</em> hierarchicke sledovani souboru <em>/ /</em> SUMA, 7/1993 */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"macros.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"utlcompo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static void ZaradS( SekvSoubor *s )<br>{<br>Pisen *P=SekvenceS(s)-&gt;Pis;<br>s-&gt;next=P-&gt;SSoubory;<br>P-&gt;SSoubory=s;<br>}<br>static void VypustS( SekvSoubor *s )<br>{<br>Pisen *P=SekvenceS(s)-&gt;Pis;<br>SekvSoubor **q;<br>for( q=&amp;P-&gt;SSoubory; *q!=s; q=&amp;(<em>q)-&gt;next ) Plati(</em>q);<br>*q=s-&gt;next;<br>s-&gt;next=NULL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void OtevriS( SekvSoubor *s, Sekvence *S, int Mode )<br>{<br>s-&gt;SUroven=0;<br>s-&gt;SHierarch=False;<br>OtevriK(&amp;s-&gt;ss[0],S,Mode);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void RozdvojS( SekvSoubor *s, const SekvSoubor *o )<br>{<br>int i;<br>s-&gt;SHierarch=o-&gt;SHierarch;<br>s-&gt;SUroven=o-&gt;SUroven;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ ) RozdvojK(&amp;s-&gt;ss[i],&amp;o-&gt;ss[i]);<br>if( s-&gt;SHierarch ) ZaradS(s);<br>}<br>void RZapisS( SekvSoubor *s, const SekvSoubor *o )<br>{<br>int i;<br>s-&gt;SHierarch=o-&gt;SHierarch;<br>s-&gt;SUroven=o-&gt;SUroven;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ ) RZapisK(&amp;s-&gt;ss[i],&amp;o-&gt;ss[i]);<br>if( s-&gt;SHierarch ) ZaradS(s);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void ZavriS( SekvSoubor *s )<br>{<br>int i;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ ) ZavriK(&amp;s-&gt;ss[i]);<br>if( s-&gt;SHierarch ) VypustS(s);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void UrovenS( SekvSoubor *s, int u )<br>{<br>int i;<br>Plati( u&lt;=s-&gt;SUroven );<br>for( i=u+1; i&lt;=s-&gt;SUroven; i++ ) ZavriK(&amp;s-&gt;ss[i]);<br>s-&gt;SUroven=u;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void RezimS( SekvSoubor *s, Flag Hierarch )<br>{<br>if( Hierarch!=s-&gt;SHierarch )<br>{<br>s-&gt;SHierarch=Hierarch;<br>UrovenS(s,0);<br>if( Hierarch ) ZaradS(s);<br>else VypustS(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>SekvKursor *AktK( SekvSoubor *s )<br>{<br>return &amp;s-&gt;ss[s-&gt;SUroven];<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>JednItem *BufS( SekvSoubor *s )<br>{<br>return s-&gt;ss[s-&gt;SUroven].Buf;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* pri pruchodu sekvenci ukazuje nadrazeny Kursor na PVyvolej */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void SetDef( SekvSoubor <em>s ) { /</em> nastaveni parametru podle kontextu */<br>SekvKursor *k;<br>if( s-&gt;SUroven&lt;=0 ) return; k=&amp;s-&gt;ss[s-&gt;SUroven];<br>SetKontext(k,k-1);<br>/<em>k-&gt;K.KteryTakt=k-&gt;A.KteryTakt=0;</em>/<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err CtiS( SekvSoubor *s )<br>{<br>SekvKursor *k=AktK(s);<br>Err ret=CtiK(k);<br>if( !s-&gt;SHierarch ) return ret;<br>if( ret!=EOK )<br>{ /* Konec partu */<br>if( s-&gt;SUroven&gt;0 )<br>{<br>JednItem *B;<br>SekvKursor <em>k=&amp;s-&gt;ss[s-&gt;SUroven]; int SekI=k-&gt;S-&gt;INazev; ZavriK(k); s-&gt;SUroven--; k=AktK(s); ret=TestK(k); /</em> preskok PVyvolej */<br>if( ret==EOK )<br>{<br>B=k-&gt;Buf;<br>Plati( B[0]==PVyvolej &amp;&amp; B[1]==SekI );<br>PlatiProc( JCtiK(k), ==EOK );<br>B[0]=PKonSekv;<br>if( k-&gt;A.VTaktu==0 ) k-&gt;A.VTaktu+=k-&gt;A.Takt,k-&gt;A.KteryTakt--;<br>while( k-&gt;A.VTaktu&gt;k-&gt;A.Takt ) k-&gt;A.VTaktu-=k-&gt;A.Takt,k-&gt;A.KteryTakt++;<br>}<br>}<br>}<br>else if( k-&gt;Buf[0]==PVyvolej )<br>{<br>if( s-&gt;SUrovenS-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( S )<br>{<br>JednItem *B;<br>SekvKursor *n;<br>s-&gt;SUroven++;<br>n=AktK(s);<br>OtevriK(n,S,k-&gt;Mode);<br>B=BufS(s);<br>*B++=PZacSekv;<br>*B++=k-&gt;Buf[1];<br>*B++=k-&gt;Buf[2];<br><em>B++=0; PlatiProc( ZpatkyK(k), ==EOK ); /</em> zpet na PVyvolej */<br>SetDef(s);<br>}<br>}<br>}<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int CmpPos( const StrPosT *P, const StrPosT *p )<br>{<br>long d;<br>d=P-&gt;p[0]-p-&gt;p[0];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>d=P-&gt;p[1]-p-&gt;p[1];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>d=P-&gt;p[2]-p-&gt;p[2];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>d=P-&gt;p[3]-p-&gt;p[3];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>/* pozor - predp. MaxSekVnor==4 */<br>#ifdef <strong>TURBOC</strong><br>#if MaxSekVnor!=4<br>#error \"OptimKonstr\"<br>#endif<br>#endif<br>return 0;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* i fiktivni jednotky pri rek. pruchodu */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag Neumele( JednItem Druh )<br>{<br>return<br>(<br>Druh!=PTakt &amp;&amp; Druh!=PDvojTaktUm &amp;&amp; Druh!=PUmPauza<br>);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* jen opravdove jednotky */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag Skutecne( JednItem Druh )<br>{<br>return Neumele(Druh) &amp;&amp; Druh!=PZacSekv &amp;&amp; Druh!=PKonSekv;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err JCtiS( SekvSoubor *s )<br>{<br>Err ret;<br>if( !s-&gt;SHierarch ) return JCtiK(AktK(s));<br>ret=CtiS(s);<br>if( ret==OK &amp;&amp; !Neumele(BufS(s)[0]) ) ret=CtiS(s);<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err KopyS( SekvSoubor *d, SekvSoubor *s )<br>{<br>SekvKursor *sk=AktK(s);<br>Err ret=TestK(sk);<br>if( ret&lt;EOK ) return ret;<br>if( BufK(sk)[0]!=PKonSekv ) return KopyK(AktK(d),sk);<br>else return ECAN;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err TestS( SekvSoubor *s )<br>{<br>SekvKursor *k=AktK(s);<br>Err ret=TestK(k);<br>if( !s-&gt;SHierarch ) return ret;<br>if( ret!=OK )<br>{<br>if( s-&gt;SUroven&gt;0 )<br>{<br>JednItem *B=k-&gt;Buf;<br>*B++=PKonSekv;<br><em>B++=</em>B++=*B++=0;<br>ret=OK;<br>}<br>}<br>else if( k-&gt;Buf[0]==PVyvolej ) k-&gt;Buf[0]=PZacSekv;<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err ZpatkyS( SekvSoubor *s )<br>{<br>SekvKursor *k=AktK(s);<br>Err ret=ZpatkyK(k);<br>if( !s-&gt;SHierarch ) return ret;<br>if( ret!=OK )<br>{<br>if( s-&gt;SUroven&gt;0 )<br>{<br>JednItem *B;<br>SekvKursor *k=&amp;s-&gt;ss[s-&gt;SUroven];<br>int SekI=k-&gt;S-&gt;INazev;<br>ZavriK(k);<br>s-&gt;SUroven--;<br>k=AktK(s);<br>ret=TestK(k);<br>Plati( ret==OK );<br>Plati( k-&gt;Buf[0]==PVyvolej );<br>Plati( k-&gt;Buf[1]==SekI );<br>B=k-&gt;Buf;<br>B[0]=PZacSekv;<br>}<br>}<br>else if( k-&gt;Buf[0]==PVyvolej )<br>{<br>if( s-&gt;SUrovenS-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( S )<br>{<br>JednItem *B;<br>SekvKursor *n;<br>s-&gt;SUroven++;<br>n=AktK(s);<br>OtevriK(n,S,k-&gt;Mode);<br>SetDef(s);<br>NajdiCasK(n,0x7fffffffL); /* presun na konec */<br>B=n-&gt;Buf;<br>*B++=PKonSekv;<br>*B++=k-&gt;Buf[1];<br>*B++=k-&gt;Buf[2];<br>*B++=0;<br>}<br>}<br>}<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CasT DelkaS( SekvSoubor *s )<br>{<br>return DelkaK(AktK(s));<br>}<br>CasT DelkaSF( SekvSoubor *s )<br>{<br>return DelkaKF(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Kontext *KKontextS( SekvSoubor *s )<br>{<br>return KKontextK(&amp;s-&gt;ss[0]);<br>}<br>Kontext *AKontextS( SekvSoubor *s )<br>{<br>return AKontextK(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int NajdiZacRepS( SekvSoubor *s )<br>{<br>return NajdiZacRepK(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* pri vymazani PVyvolej za zavrou vsechny <em>/ /</em> podrizene kursory, ktere ocekavaji navrat tamtez <em>/ /</em> pri vymazani sekvence podobne */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* reakce na vymazani sekvence */<br>void ZavriVolajici( Sekvence *S )<br>{<br>SekvSoubor *t;<br>for( t=S-&gt;Pis-&gt;SSoubory; t; t=t-&gt;next ) if( t-&gt;SHierarch )<br>{<br>int i;<br>for( i=0; i&lt;=t-&gt;SUroven; i++ )<br>{<br>SekvKursor *at=&amp;t-&gt;ss[i];<br>Sekvence <em>T=SekvenceK(at); if( T==S ) { /</em> nutno odstranit dalsi vrstvy <em>/ Plati( i&gt;0 ); UrovenS(t,i-1); /</em> mohlo by nekomu vadit, ze s nim hneme? !!! */<br>break;<br>}<br>}<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* reakce na vymazani volani sekvence */<br>void ZavriVolajiciZ( SekvKursor *s )<br>{<br>Sekvence *S=SekvenceK(s);<br>SekvSoubor *t;<br>for( t=S-&gt;Pis-&gt;SSoubory; t; t=t-&gt;next )<br>{<br>int i;<br>for( i=0; iSUroven; i++ ) /* i==SUroven uz nema cenu zkoumat - tam uz nic nemuze vadit */<br>{<br>SekvKursor *at=&amp;t-&gt;ss[i];<br>Sekvence <em>T=SekvenceK(at); if( T==S &amp;&amp; KdeJeK(at)==KdeJeK(s) ) { /</em> nutno odstranit dalsi vrstvy <em>/ UrovenS(t,i); /</em> mohlo by nekomu vadit, ze s nim hneme? !!! */<br>break;<br>}<br>}<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err MazS( SekvSoubor *s )<br>{<br>Err ret;<br>JednItem *B;<br>ret=TestS(s);<br>if( ret!=EOK ) return ret;<br>B=BufS(s);<br>if( B[0]==PKonSekv ) return ECAN;<br>return MazK(AktK(s));<br>}<br>Err VlozS( SekvSoubor *s )<br>{<br>if( !Skutecne(BufS(s)[0]) ) return ERR;<br>return VlozK(AktK(s));<br>}<br>Err PisS( SekvSoubor *s )<br>{<br>if( !Skutecne(BufS(s)[0]) ) return ERR;<br>return PisK(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err KdeJeS( SekvSoubor *s, StrPosT *Pos )<br>{<br>int i;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ )<br>{<br>Pos-&gt;p[i]=KdeJeK(&amp;s-&gt;ss[i]);<br>}<br>for( ; ip[i]=-1;<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CasT CasJeS( SekvSoubor *s )<br>{<br>int i;<br>CasT c=0;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ )<br>{<br>c+=CasJeK(&amp;s-&gt;ss[i]);<br>}<br>return c;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiS( SekvSoubor *s, const StrPosT *Pos )<br>{<br>Sekvence *S;<br>SekvKursor *k=&amp;s-&gt;ss[0];<br>Err ret;<br>UrovenS(s,0);<br>if( !s-&gt;SHierarch ) return NajdiK(k,Pos-&gt;p[0]);<br>for(;;)<br>{<br>int M;<br>ret=NajdiK(k,Pos-&gt;p[s-&gt;SUroven]);<br>if( ret!=EOK || s-&gt;SUroven&gt;=MaxSekVnor-1 ) return ret;<br>if( Pos-&gt;p[s-&gt;SUroven+1]&lt;0 ) return EOK; ret=TestK(k); if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return ret;<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return ret;<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiCasS( SekvSoubor *s, CasT Cas )<br>{<br>Sekvence *S;<br>CasT ca;<br>SekvKursor *k=&amp;s-&gt;ss[0];<br>Err ret;<br>if( !s-&gt;SHierarch ) return NajdiCasK(k,Cas);<br>UrovenS(s,0);<br>for(;;)<br>{<br>int M;<br>ret=NajdiSCasK(k,Cas);<br>if( ret!=EOK || s-&gt;SUroven&gt;=MaxSekVnor-1 ) return ret;<br>ca=CasJeK(k);<br>if( ca==Cas ) return NajdiCasK(k,Cas);<br>ret=TestK(k);<br>if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return NajdiCasK(k,Cas);<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return NajdiCasK(k,ca);<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>Cas-=ca;<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiSCasS( SekvSoubor *s, CasT Cas )<br>{<br>Sekvence *S;<br>CasT ca;<br>SekvKursor *k=&amp;s-&gt;ss[0];<br>Err ret;<br>UrovenS(s,0);<br>if( !s-&gt;SHierarch ) return NajdiSCasK(k,Cas);<br>for(;;)<br>{<br>int M;<br>ret=NajdiSCasK(k,Cas);<br>if( s-&gt;SUroven&gt;=MaxSekVnor-1 || ret!=EOK ) return ret;<br>ca=CasJeK(k);<br>Cas-=ca;<br>Plati( Cas&gt;=0 );<br>ret=TestK(k);<br>if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return ret;<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return ret;<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiTaktS( SekvSoubor *s, int Takt )<br>{<br>Sekvence *S;<br>/<em>int ca;</em>/<br>SekvKursor <em>k=&amp;s-&gt;ss[0]; Err ret; UrovenS(s,0); if( !s-&gt;SHierarch ) return NajdiTaktK(k,Takt); for(;;) { int M; ret=NajdiTaktK(k,Takt); if( s-&gt;SUroven&gt;=MaxSekVnor-1 || ret!=EOK ) return ret; /</em><br>ca=AKontextK(k)-&gt;KteryTakt;<br>Takt-=ca;<br><em>/ /</em> i dovnitr se cisluje stejne */<br>Plati( Takt&gt;=0 );<br>ret=TestK(k);<br>if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return ret;<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return ret;<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* musi byt ve stejne sekvenci a oba stejne Hierarch */<br>void NajdiPosS( SekvSoubor *s, const SekvSoubor *o )<br>{<br>int Mode=AktK(s)-&gt;Mode;<br>Plati( SekvenceS(s)==SekvenceS(o) );<br>Plati( s-&gt;SHierarch==o-&gt;SHierarch );<br>ZavriS(s);<br>if( Mode==Cteni ) RozdvojS(s,o);<br>else RZapisS(s,o);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err StatusS( SekvSoubor *s )<br>{<br>if( s-&gt;SUroven&gt;0 ) return OK;<br>return (Err)s-&gt;ss[0].Status;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag BezChybyS( SekvSoubor *s )<br>{<br>return StatusS(s)==OK || StatusS(s)==ZAC || StatusS(s)==KON;<br>}</p>\n<!-- /wp:paragraph -->', 'REKCOMPO.cpp', '', 'publish', 'open', 'open', '', 'rekcompo-cpp', '', '', '2023-05-13 13:34:10', '2023-05-13 13:34:10', '', 0, 'http://xboxcode.hstn.me/?p=66', 0, 'post', '', 0),
(67, 1, '2023-05-13 13:34:09', '2023-05-13 13:34:09', '<!-- wp:paragraph -->\n<p>/* Componium - zachazeni s vnitrnim formatem <em>/ /</em> hierarchicke sledovani souboru <em>/ /</em> SUMA, 7/1993 */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"macros.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"utlcompo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static void ZaradS( SekvSoubor *s )<br>{<br>Pisen *P=SekvenceS(s)-&gt;Pis;<br>s-&gt;next=P-&gt;SSoubory;<br>P-&gt;SSoubory=s;<br>}<br>static void VypustS( SekvSoubor *s )<br>{<br>Pisen *P=SekvenceS(s)-&gt;Pis;<br>SekvSoubor **q;<br>for( q=&amp;P-&gt;SSoubory; *q!=s; q=&amp;(<em>q)-&gt;next ) Plati(</em>q);<br>*q=s-&gt;next;<br>s-&gt;next=NULL;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void OtevriS( SekvSoubor *s, Sekvence *S, int Mode )<br>{<br>s-&gt;SUroven=0;<br>s-&gt;SHierarch=False;<br>OtevriK(&amp;s-&gt;ss[0],S,Mode);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void RozdvojS( SekvSoubor *s, const SekvSoubor *o )<br>{<br>int i;<br>s-&gt;SHierarch=o-&gt;SHierarch;<br>s-&gt;SUroven=o-&gt;SUroven;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ ) RozdvojK(&amp;s-&gt;ss[i],&amp;o-&gt;ss[i]);<br>if( s-&gt;SHierarch ) ZaradS(s);<br>}<br>void RZapisS( SekvSoubor *s, const SekvSoubor *o )<br>{<br>int i;<br>s-&gt;SHierarch=o-&gt;SHierarch;<br>s-&gt;SUroven=o-&gt;SUroven;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ ) RZapisK(&amp;s-&gt;ss[i],&amp;o-&gt;ss[i]);<br>if( s-&gt;SHierarch ) ZaradS(s);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void ZavriS( SekvSoubor *s )<br>{<br>int i;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ ) ZavriK(&amp;s-&gt;ss[i]);<br>if( s-&gt;SHierarch ) VypustS(s);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void UrovenS( SekvSoubor *s, int u )<br>{<br>int i;<br>Plati( u&lt;=s-&gt;SUroven );<br>for( i=u+1; i&lt;=s-&gt;SUroven; i++ ) ZavriK(&amp;s-&gt;ss[i]);<br>s-&gt;SUroven=u;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void RezimS( SekvSoubor *s, Flag Hierarch )<br>{<br>if( Hierarch!=s-&gt;SHierarch )<br>{<br>s-&gt;SHierarch=Hierarch;<br>UrovenS(s,0);<br>if( Hierarch ) ZaradS(s);<br>else VypustS(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>SekvKursor *AktK( SekvSoubor *s )<br>{<br>return &amp;s-&gt;ss[s-&gt;SUroven];<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>JednItem *BufS( SekvSoubor *s )<br>{<br>return s-&gt;ss[s-&gt;SUroven].Buf;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* pri pruchodu sekvenci ukazuje nadrazeny Kursor na PVyvolej */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void SetDef( SekvSoubor <em>s ) { /</em> nastaveni parametru podle kontextu */<br>SekvKursor *k;<br>if( s-&gt;SUroven&lt;=0 ) return; k=&amp;s-&gt;ss[s-&gt;SUroven];<br>SetKontext(k,k-1);<br>/<em>k-&gt;K.KteryTakt=k-&gt;A.KteryTakt=0;</em>/<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err CtiS( SekvSoubor *s )<br>{<br>SekvKursor *k=AktK(s);<br>Err ret=CtiK(k);<br>if( !s-&gt;SHierarch ) return ret;<br>if( ret!=EOK )<br>{ /* Konec partu */<br>if( s-&gt;SUroven&gt;0 )<br>{<br>JednItem *B;<br>SekvKursor <em>k=&amp;s-&gt;ss[s-&gt;SUroven]; int SekI=k-&gt;S-&gt;INazev; ZavriK(k); s-&gt;SUroven--; k=AktK(s); ret=TestK(k); /</em> preskok PVyvolej */<br>if( ret==EOK )<br>{<br>B=k-&gt;Buf;<br>Plati( B[0]==PVyvolej &amp;&amp; B[1]==SekI );<br>PlatiProc( JCtiK(k), ==EOK );<br>B[0]=PKonSekv;<br>if( k-&gt;A.VTaktu==0 ) k-&gt;A.VTaktu+=k-&gt;A.Takt,k-&gt;A.KteryTakt--;<br>while( k-&gt;A.VTaktu&gt;k-&gt;A.Takt ) k-&gt;A.VTaktu-=k-&gt;A.Takt,k-&gt;A.KteryTakt++;<br>}<br>}<br>}<br>else if( k-&gt;Buf[0]==PVyvolej )<br>{<br>if( s-&gt;SUrovenS-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( S )<br>{<br>JednItem *B;<br>SekvKursor *n;<br>s-&gt;SUroven++;<br>n=AktK(s);<br>OtevriK(n,S,k-&gt;Mode);<br>B=BufS(s);<br>*B++=PZacSekv;<br>*B++=k-&gt;Buf[1];<br>*B++=k-&gt;Buf[2];<br><em>B++=0; PlatiProc( ZpatkyK(k), ==EOK ); /</em> zpet na PVyvolej */<br>SetDef(s);<br>}<br>}<br>}<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int CmpPos( const StrPosT *P, const StrPosT *p )<br>{<br>long d;<br>d=P-&gt;p[0]-p-&gt;p[0];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>d=P-&gt;p[1]-p-&gt;p[1];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>d=P-&gt;p[2]-p-&gt;p[2];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>d=P-&gt;p[3]-p-&gt;p[3];<br>if( d&lt;0 ) return -1;else if( d&gt;0 ) return +1;<br>/* pozor - predp. MaxSekVnor==4 */<br>#ifdef <strong>TURBOC</strong><br>#if MaxSekVnor!=4<br>#error \"OptimKonstr\"<br>#endif<br>#endif<br>return 0;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* i fiktivni jednotky pri rek. pruchodu */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag Neumele( JednItem Druh )<br>{<br>return<br>(<br>Druh!=PTakt &amp;&amp; Druh!=PDvojTaktUm &amp;&amp; Druh!=PUmPauza<br>);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* jen opravdove jednotky */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag Skutecne( JednItem Druh )<br>{<br>return Neumele(Druh) &amp;&amp; Druh!=PZacSekv &amp;&amp; Druh!=PKonSekv;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err JCtiS( SekvSoubor *s )<br>{<br>Err ret;<br>if( !s-&gt;SHierarch ) return JCtiK(AktK(s));<br>ret=CtiS(s);<br>if( ret==OK &amp;&amp; !Neumele(BufS(s)[0]) ) ret=CtiS(s);<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err KopyS( SekvSoubor *d, SekvSoubor *s )<br>{<br>SekvKursor *sk=AktK(s);<br>Err ret=TestK(sk);<br>if( ret&lt;EOK ) return ret;<br>if( BufK(sk)[0]!=PKonSekv ) return KopyK(AktK(d),sk);<br>else return ECAN;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err TestS( SekvSoubor *s )<br>{<br>SekvKursor *k=AktK(s);<br>Err ret=TestK(k);<br>if( !s-&gt;SHierarch ) return ret;<br>if( ret!=OK )<br>{<br>if( s-&gt;SUroven&gt;0 )<br>{<br>JednItem *B=k-&gt;Buf;<br>*B++=PKonSekv;<br><em>B++=</em>B++=*B++=0;<br>ret=OK;<br>}<br>}<br>else if( k-&gt;Buf[0]==PVyvolej ) k-&gt;Buf[0]=PZacSekv;<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err ZpatkyS( SekvSoubor *s )<br>{<br>SekvKursor *k=AktK(s);<br>Err ret=ZpatkyK(k);<br>if( !s-&gt;SHierarch ) return ret;<br>if( ret!=OK )<br>{<br>if( s-&gt;SUroven&gt;0 )<br>{<br>JednItem *B;<br>SekvKursor *k=&amp;s-&gt;ss[s-&gt;SUroven];<br>int SekI=k-&gt;S-&gt;INazev;<br>ZavriK(k);<br>s-&gt;SUroven--;<br>k=AktK(s);<br>ret=TestK(k);<br>Plati( ret==OK );<br>Plati( k-&gt;Buf[0]==PVyvolej );<br>Plati( k-&gt;Buf[1]==SekI );<br>B=k-&gt;Buf;<br>B[0]=PZacSekv;<br>}<br>}<br>else if( k-&gt;Buf[0]==PVyvolej )<br>{<br>if( s-&gt;SUrovenS-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( S )<br>{<br>JednItem *B;<br>SekvKursor *n;<br>s-&gt;SUroven++;<br>n=AktK(s);<br>OtevriK(n,S,k-&gt;Mode);<br>SetDef(s);<br>NajdiCasK(n,0x7fffffffL); /* presun na konec */<br>B=n-&gt;Buf;<br>*B++=PKonSekv;<br>*B++=k-&gt;Buf[1];<br>*B++=k-&gt;Buf[2];<br>*B++=0;<br>}<br>}<br>}<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CasT DelkaS( SekvSoubor *s )<br>{<br>return DelkaK(AktK(s));<br>}<br>CasT DelkaSF( SekvSoubor *s )<br>{<br>return DelkaKF(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Kontext *KKontextS( SekvSoubor *s )<br>{<br>return KKontextK(&amp;s-&gt;ss[0]);<br>}<br>Kontext *AKontextS( SekvSoubor *s )<br>{<br>return AKontextK(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int NajdiZacRepS( SekvSoubor *s )<br>{<br>return NajdiZacRepK(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* pri vymazani PVyvolej za zavrou vsechny <em>/ /</em> podrizene kursory, ktere ocekavaji navrat tamtez <em>/ /</em> pri vymazani sekvence podobne */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* reakce na vymazani sekvence */<br>void ZavriVolajici( Sekvence *S )<br>{<br>SekvSoubor *t;<br>for( t=S-&gt;Pis-&gt;SSoubory; t; t=t-&gt;next ) if( t-&gt;SHierarch )<br>{<br>int i;<br>for( i=0; i&lt;=t-&gt;SUroven; i++ )<br>{<br>SekvKursor *at=&amp;t-&gt;ss[i];<br>Sekvence <em>T=SekvenceK(at); if( T==S ) { /</em> nutno odstranit dalsi vrstvy <em>/ Plati( i&gt;0 ); UrovenS(t,i-1); /</em> mohlo by nekomu vadit, ze s nim hneme? !!! */<br>break;<br>}<br>}<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* reakce na vymazani volani sekvence */<br>void ZavriVolajiciZ( SekvKursor *s )<br>{<br>Sekvence *S=SekvenceK(s);<br>SekvSoubor *t;<br>for( t=S-&gt;Pis-&gt;SSoubory; t; t=t-&gt;next )<br>{<br>int i;<br>for( i=0; iSUroven; i++ ) /* i==SUroven uz nema cenu zkoumat - tam uz nic nemuze vadit */<br>{<br>SekvKursor *at=&amp;t-&gt;ss[i];<br>Sekvence <em>T=SekvenceK(at); if( T==S &amp;&amp; KdeJeK(at)==KdeJeK(s) ) { /</em> nutno odstranit dalsi vrstvy <em>/ UrovenS(t,i); /</em> mohlo by nekomu vadit, ze s nim hneme? !!! */<br>break;<br>}<br>}<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err MazS( SekvSoubor *s )<br>{<br>Err ret;<br>JednItem *B;<br>ret=TestS(s);<br>if( ret!=EOK ) return ret;<br>B=BufS(s);<br>if( B[0]==PKonSekv ) return ECAN;<br>return MazK(AktK(s));<br>}<br>Err VlozS( SekvSoubor *s )<br>{<br>if( !Skutecne(BufS(s)[0]) ) return ERR;<br>return VlozK(AktK(s));<br>}<br>Err PisS( SekvSoubor *s )<br>{<br>if( !Skutecne(BufS(s)[0]) ) return ERR;<br>return PisK(AktK(s));<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err KdeJeS( SekvSoubor *s, StrPosT *Pos )<br>{<br>int i;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ )<br>{<br>Pos-&gt;p[i]=KdeJeK(&amp;s-&gt;ss[i]);<br>}<br>for( ; ip[i]=-1;<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CasT CasJeS( SekvSoubor *s )<br>{<br>int i;<br>CasT c=0;<br>for( i=0; i&lt;=s-&gt;SUroven; i++ )<br>{<br>c+=CasJeK(&amp;s-&gt;ss[i]);<br>}<br>return c;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiS( SekvSoubor *s, const StrPosT *Pos )<br>{<br>Sekvence *S;<br>SekvKursor *k=&amp;s-&gt;ss[0];<br>Err ret;<br>UrovenS(s,0);<br>if( !s-&gt;SHierarch ) return NajdiK(k,Pos-&gt;p[0]);<br>for(;;)<br>{<br>int M;<br>ret=NajdiK(k,Pos-&gt;p[s-&gt;SUroven]);<br>if( ret!=EOK || s-&gt;SUroven&gt;=MaxSekVnor-1 ) return ret;<br>if( Pos-&gt;p[s-&gt;SUroven+1]&lt;0 ) return EOK; ret=TestK(k); if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return ret;<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return ret;<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiCasS( SekvSoubor *s, CasT Cas )<br>{<br>Sekvence *S;<br>CasT ca;<br>SekvKursor *k=&amp;s-&gt;ss[0];<br>Err ret;<br>if( !s-&gt;SHierarch ) return NajdiCasK(k,Cas);<br>UrovenS(s,0);<br>for(;;)<br>{<br>int M;<br>ret=NajdiSCasK(k,Cas);<br>if( ret!=EOK || s-&gt;SUroven&gt;=MaxSekVnor-1 ) return ret;<br>ca=CasJeK(k);<br>if( ca==Cas ) return NajdiCasK(k,Cas);<br>ret=TestK(k);<br>if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return NajdiCasK(k,Cas);<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return NajdiCasK(k,ca);<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>Cas-=ca;<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiSCasS( SekvSoubor *s, CasT Cas )<br>{<br>Sekvence *S;<br>CasT ca;<br>SekvKursor *k=&amp;s-&gt;ss[0];<br>Err ret;<br>UrovenS(s,0);<br>if( !s-&gt;SHierarch ) return NajdiSCasK(k,Cas);<br>for(;;)<br>{<br>int M;<br>ret=NajdiSCasK(k,Cas);<br>if( s-&gt;SUroven&gt;=MaxSekVnor-1 || ret!=EOK ) return ret;<br>ca=CasJeK(k);<br>Cas-=ca;<br>Plati( Cas&gt;=0 );<br>ret=TestK(k);<br>if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return ret;<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return ret;<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err NajdiTaktS( SekvSoubor *s, int Takt )<br>{<br>Sekvence *S;<br>/<em>int ca;</em>/<br>SekvKursor <em>k=&amp;s-&gt;ss[0]; Err ret; UrovenS(s,0); if( !s-&gt;SHierarch ) return NajdiTaktK(k,Takt); for(;;) { int M; ret=NajdiTaktK(k,Takt); if( s-&gt;SUroven&gt;=MaxSekVnor-1 || ret!=EOK ) return ret; /</em><br>ca=AKontextK(k)-&gt;KteryTakt;<br>Takt-=ca;<br><em>/ /</em> i dovnitr se cisluje stejne */<br>Plati( Takt&gt;=0 );<br>ret=TestK(k);<br>if( ret!=EOK || k-&gt;Buf[0]!=PVyvolej ) return ret;<br>S=NajdiSekvBI(k-&gt;S-&gt;Pis,k-&gt;Buf[1],k-&gt;Buf[2]);<br>if( !S ) return ret;<br>s-&gt;SUroven++;<br>M=k-&gt;Mode;<br>k=AktK(s);<br>OtevriK(k,S,M);<br>SetDef(s);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* musi byt ve stejne sekvenci a oba stejne Hierarch */<br>void NajdiPosS( SekvSoubor *s, const SekvSoubor *o )<br>{<br>int Mode=AktK(s)-&gt;Mode;<br>Plati( SekvenceS(s)==SekvenceS(o) );<br>Plati( s-&gt;SHierarch==o-&gt;SHierarch );<br>ZavriS(s);<br>if( Mode==Cteni ) RozdvojS(s,o);<br>else RZapisS(s,o);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err StatusS( SekvSoubor *s )<br>{<br>if( s-&gt;SUroven&gt;0 ) return OK;<br>return (Err)s-&gt;ss[0].Status;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag BezChybyS( SekvSoubor *s )<br>{<br>return StatusS(s)==OK || StatusS(s)==ZAC || StatusS(s)==KON;<br>}</p>\n<!-- /wp:paragraph -->', 'REKCOMPO.cpp', '', 'inherit', 'closed', 'closed', '', '66-revision-v1', '', '', '2023-05-13 13:34:09', '2023-05-13 13:34:09', '', 66, 'http://xboxcode.hstn.me/?p=67', 0, 'revision', '', 0),
(68, 1, '2023-05-13 13:34:44', '2023-05-13 13:34:44', '<!-- wp:paragraph -->\n<p>/* Componium - obsluha ToolBoxu <em>/ /</em> SUMA 9/1993-2/1994 */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"ramcompo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"compo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static int ToolHan=-1; /* LHan toolboxu */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag ToolEditB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>EdModeO=EdMode=DEditace;DskMode();<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>static Flag ToolMazB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>EdModeO=EdMode=DMazani;DskMode();<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>static Flag ToolTonB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>Flag TestM=StavMenu(Menu,VSEHLAI);<br>OznacMenu(Menu,VSEHLAI,True);<br>DoMenu(TONINAI,False);<br>OznacMenu(Menu,VSEHLAI,TestM);<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>static Flag ToolTempB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>Flag TestM=StavMenu(Menu,VSEHLAI);<br>OznacMenu(Menu,VSEHLAI,True);<br>DoMenu(TEMPOI,False);<br>OznacMenu(Menu,VSEHLAI,TestM);<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>static Flag ToolTaktB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>Flag TestM=StavMenu(Menu,VSEHLAI);<br>OznacMenu(Menu,VSEHLAI,True);<br>DoMenu(TAKTI,False);<br>OznacMenu(Menu,VSEHLAI,TestM);<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag ToolPisB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>DoMenu(ULOZDOI,False);<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>Flag ToolHrajB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>DoMenu(ZAHRPI,False);<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>static Flag AskBlokRepet( int LHan )<br>{<br>int NR=AskRepetD(2,NULL);<br>if( NR&gt;=OK )<br>{<br>BlokRepetice(LHan,NR);<br>IgnKresli(LHan);<br>return True;<br>}<br>return False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag ToolRepB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>int LHan=VrchniSekv();<br>if( LHan&gt;=0 )<br>{<br>OknoInfo *OI=OInfa[LHan];<br>if( OI-&gt;ZBStopa&gt;=0 ) AskBlokRepet(LHan); /* existuje blok -&gt; lze ho editovat */<br>}<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}<br>static Flag ToolPartB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>DoMenu(NOTYI,False);<br>(void)Bt,(void)Dvoj,(void)Asc;return False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag ToolCtiB( OBJECT *Bt, Flag Dvoj, int Asc )<br>{<br>PovolCinMenu(False);<br>if( ZavriVse() )<br>{<br>DoMenu(OTEVRI,False);<br>}<br>PovolCinMenu(True);<br>(void)Bt,(void)Dvoj,(void)Asc;<br>return False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag ToolChceTopnout( int LHan, int MX, int MY )<br>{<br>if( MX&gt;=0 &amp;&amp; MY&gt;=0 )<br>{<br>DialStiskAPust(LHan,False,False,MX,MY);<br>return False; /* netopat */<br>}<br>return True;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void SetState( int I, Flag Yes, int Mask )<br>{<br>WindDial *OI=OInfa[ToolHan];<br>OBJECT *O=&amp;OI-&gt;D.Form[I];<br>int OS=O-&gt;ob_state,NS=OS;<br>if( !Yes ) NS&amp;=~Mask;<br>else NS|=Mask;<br>if( NS!=OS )<br>{<br>WindRect W;<br>O-&gt;ob_state=NS;<br>ObjcRect(OI-&gt;D.Form,I,&amp;W);<br>SendDraw(ApId,Okna[ToolHan]-&gt;handle,&amp;W);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void ToolSelect( int I, Flag Yes )<br>{<br>SetState(I,Yes,SELECTED);<br>}<br>void ToolDraw( int I )<br>{<br>WindRect W;<br>WindDial *OI=OInfa[ToolHan];<br>ObjcRect(OI-&gt;D.Form,I,&amp;W);<br>SendDraw(ApId,Okna[ToolHan]-&gt;handle,&amp;W);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void DskMode( void )<br>{<br>if( ToolHan&gt;=0 )<br>{<br>SetState(DSKEDITB,EdModeO==DEditace,SELECTED);<br>SetState(DSKMAZB,EdModeO==DMazani,SELECTED);<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void CNastavDesktop( void )<br>{<br>/* ve skutecnosti s desktopem nic nedela - oznaceni jen z historie */<br>if( ToolHan&gt;=0 )<br>{<br>WindDial *OI=OInfa[ToolHan];<br>OBJECT *F=OI-&gt;D.Form;<br>int LHan=VrchniSekv();<br>Flag SekvEnab=LHan&gt;=0;<br>static const int Desks[]=<br>{<br>DSKTONB,DSKTAKTB,<br>DSKEDITB,DSKTEMPB,<br>DSKMAZB,DSKHRAJB,DSKPISB,DSKPARTB,<br>0<br>};<br>const int *I;<br>Flag DrawF=False;<br>BegUpdate();<br>for( I=Desks; <em>I; I++ ) { if( Test(&amp;F[</em>I],DISABLED)==SekvEnab ) DrawF=True;<br>Set01(&amp;F[*I],DISABLED,!SekvEnab);<br>}<br>{<br>OknoInfo *OI=OInfa[LHan];<br>Flag FE=SekvEnab &amp;&amp; OI-&gt;ZBStopa&gt;=0;<br>if( DrawF ) Set01(&amp;F[DSKREPB],DISABLED,!FE);<br>else SetState(DSKREPB,!FE,DISABLED);<br>}<br>if( DrawF ) ToolDraw(0);<br>EndUpdate();<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Button ToolTlac[]=<br>{<br>{ToolEditB,DSKEDITB},<br>{ToolMazB,DSKMAZB},<br>{ToolTonB,DSKTONB},<br>{ToolTempB,DSKTEMPB},<br>{ToolTaktB,DSKTAKTB},<br>{ToolPisB,DSKPISB},<br>{ToolHrajB,DSKHRAJB},<br>{ToolCtiB,DSKCTIB},<br>{ToolRepB,DSKREPB},<br>{ToolPartB,DSKPARTB},<br>{NULL}<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag LogZTool( int LHan )<br>{<br>ToolHan=-1;<br>return DialLogZ(LHan);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag LogOTool( int LHan )<br>{<br>WindDial *OI=malloc(sizeof(WindDial));<br>WindRect FD;<br>Okno *O=Okna[LHan];<br>if( !OI ) {ChybaRAM();return False;}<br>OInfa[LHan]=OI;<br>OI-&gt;LHan=LHan;<br>OI-&gt;D.Form=DialCopy(RscTree(TOOLBO