,AR);<br>IS=ZavedSoub(N-&gt;Soubory[0],2);<br>if( IS&lt;0 )<br>{<br>goto Error;<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>{\n      int ni;\n      for( ni=0; ni&lt;FyzOkt*12; ni++ )\n      {\n          N-&gt;IMSoubory&#91;ni]=IS;\n      }\n}\n    N-&gt;Uzit=True;\n    ZjistiEfekty(IS);\n    UzijSoub(IS);\n}</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>c = fgetbel(f);<br>if (c&lt;0) goto Error;<br>PisZac=mallocSpc(c,\'MCoc\');<br>if( !PisZac ) goto Error;<br>if( fread(PisZac,c,1,f)!=1 ) goto Error;<br>ret=0;<br>Error:<br>if( ret&lt;0 )<br>{<br>PustPisen();<br>}<br>else<br>{<br>InitFFreq(freq);<br>RecalcFreq(FBufFreqDbl); /* pэepoџЎtej si to */<br>}<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* min. implementace playeru */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static const MPovel *_Pis;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static volatile Flag KonecHrani;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag AutoRepeat=True;<br>Flag HoverFormat = False;<br>Flag AutoEffects=True;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag TestHraje( void ){return KonecHrani;}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void InitKonec( void ){KonecHrani=False;}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static long TCnt; /* citadlo dalsi udalosti */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define GetPisW(PisB) (PisB+=2,((((unsigned char)PisB[-2])&lt;&lt;8)|(unsigned char)PisB[-1]))</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>CasT TimerADF( CasT TStep ) /* play, fwd cue */<br>{<br>int noteOffset = HoverFormat ? -12 : 0;<br>int tickCoef = HoverFormat ? 10 : 1;<br>const signed char *PisB=_Pis;<br>TCnt-=TStep;<br>while( TCnt&lt;=0 ) { int Kan; int N; switch( <em>PisB++ ) { /</em> hlasitost je -128 (Fade) .. 0 (NoteOff) ..127 <em>/ ca