(81, 1, '2023-05-13 13:42:13', '2023-05-13 13:42:13', '<!-- wp:paragraph -->\n<p>/* Componium - prekladac <em>/ /</em> SUMA 10/1992-2/1994 */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"compo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"ramcompo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"digill.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"comcompo.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define PRO_JAG 1 /* generov&nbsp;nЎ vмstup… pro Jagu&nbsp;r */</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>/* FadeOuty: 18. 8., suggested by COMPO */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define FADE_OUT 1 /* omezuje cvakani - vhodne pro samply */</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define ROVNOMERNE 0 /* omezuje hromadeni povelu - vhodne pro MIDI */</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>/* preklada se do vnitrniho formatu - je velmi podobny SMFF 0 <em>/ /</em> operace pro preklad */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum {MaxDynRep=12};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>typedef struct alokkan<br>{<br>Budik B;<br>int Kanal; /* jaky je to kanal - konstantni <em>/ int LNastroj; /</em> nastroj v kanale - cislovani v banku pisne <em>/ int FNastroj; /</em> jakм je to fyz. handle - џЎslov&nbsp;nЎ podle AktHraj */<br>struct kanprekl *LKanal; /* jaky kanal zde naposledy hral - pro dedeni <em>/ Flag Rytm; /</em> ton se sam hned vypina <em>/ Flag CekamLegato; /</em> cekam-li legato v LKanal <em>/ int StereoMin,StereoMax; /</em> stereo <em>/ int EffA,EffB; int Ton; /</em> &lt;0 zadny - to by nemelo nastat? - pak by mel byt volny? <em>/ int PBender; /</em> dos. stav PB - zatim nepouzit <em>/ Flag FadeOut; /</em> uz proveden FadeOut? */<br>struct alokkan *<em>back; /</em> zpetny odkaz <em>/ } AlokKan; /</em> informace o kanalech */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum {NAkTon=8}; /* soubШ§nмch tўn… v jednom kan&nbsp;lu */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>typedef struct kanprekl<br>{<br>Budik B; /* dedicnost <em>/ SekvSoubor p; PosuvT Pos; /</em> platne posuvky <em>/ int Opak[MaxDynRep]; /</em> repetice */<br>int RepUr;<br>int Kan;<br>AlokKan *FyzK[NAkTon];<br>} KanPrekl;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>typedef struct<br>{<br>KanPrekl KP[MaxLogKan];<br>AlokKan KA[MaxFyzKan];<br>FrontaB FrontaAK; /* ve fronte jsou obsazene kanaly <em>/ FrontaB FrontaKP; /</em> ve fronte cekaji ti, co jeste hraji <em>/ int NKan; /</em> pocet stop <em>/ int NFyzKan; /</em> pocet prehravacich kanalu <em>/ Buffer PisBuf; CasT DosCas; /</em> cas zatim dosazeny */<br>CompoErr *CErr;<br>Sekvence <em>S; Flag IgnorujNDef; long ZbyvaSynchro; int ObsKan; /</em> prave obsazeny pocet kanalu <em>/ int MaxObsKan; /</em> max., &gt;NFyzKan znamena preteceni <em>/ Flag NejakyTon; /</em> zda se vybec pouziva */<br>FILE *GenJag; /* gen. pro Jagu&nbsp;ra */<br>} PreklInfo;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void ComChyba( SekvSoubor *s, int Kan, PreklInfo *PI, const char *Alert )<br>{<br>CompoErr *CE=PI-&gt;CErr;<br>SekvSoubor k;<br>CE-&gt;Kan=Kan;<br>CE-&gt;S=PI-&gt;S;<br>RozdvojS(&amp;k,s);<br>ZpatkyS(&amp;k);<br>CE-&gt;CPos=CasJeS(&amp;k);<br>KdeJeS(&amp;k,&amp;CE-&gt;PPos);<br>ZavriS(&amp;k);<br>CE-&gt;Ukazat=True;<br>CE-&gt;Alert=Alert;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err FZapisCekani( PreklInfo *PI, long Cas )<br>{<br>if( Cas&gt;0 )<br>{<br>while( Cas&gt;0x7fff )<br>{<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(MSync&lt;&lt;8)|0) ) return ERAM; if( !ZapisW(&amp;PI-&gt;PisBuf,0x7fff) ) return ERAM;<br>Cas-=0x7fff;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.w pSync&lt;&lt;8,$7fff\\n\"); } #endif } if( !ZapisW(&amp;PI-&gt;PisBuf,(MSync&lt;&lt;8)|0) ) return ERAM; if( !ZapisW(&amp;PI-&gt;PisBuf,(int)Cas) ) return ERAM;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.w pSync&lt;&lt;8,$%lx\\n\",Cas);<br>}<br>#endif<br>}<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err ZapisCekani( PreklInfo *PI, long Cas )<br>{<br>PI-&gt;ZbyvaSynchro+=Cas;<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err NutnoSynchro( PreklInfo *PI )<br>{<br>Err ret=FZapisCekani(PI,PI-&gt;ZbyvaSynchro);<br>PI-&gt;ZbyvaSynchro=0;<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">if ROVNOMERNE</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>enum {SkoroCas=20,SkoroKrok=5};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err NutnoSkoroSynchro( PreklInfo *PI )<br>{<br>Err ret;<br>long PC=PI-&gt;ZbyvaSynchro;<br>if( PC&gt;SkoroCas ) PC-=SkoroCas;<br>ef( PC&gt;SkoroKrok ) PC=SkoroKrok;<br>ret=FZapisCekani(PI,PC);<br>PI-&gt;ZbyvaSynchro-=PC;<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">else</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define NutnoSkoroSynchro NutnoSynchro</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">if FADE_OUT</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static Err FOutKan( PreklInfo *PI, AlokKan *PA )<br>{<br>Err ret;<br>Plati( PA-&gt;Ton&gt;=0 ); /* hraje */<br>ret=NutnoSynchro(PI);if( retPisBuf,(MNotaZac&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(PA-&gt;Ton&lt;&lt;8)|0xff) ) return ERAM; PA-&gt;FadeOut=True;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pNotaZac,$%x,%d,$ff\\n\",PA-&gt;Kanal,PA-&gt;Ton);<br>}<br>#endif<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static Err VypKan( PreklInfo *PI, AlokKan *PA )<br>{<br>if( PA-&gt;Ton&gt;=0 ) /* hraje */<br>{<br>Err ret=NutnoSynchro(PI);if( retCekamLegato )<br>{<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(MNotaZac&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(PA-&gt;Ton&lt;&lt;8)|0) ) return ERAM; PA-&gt;Ton=-1;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pNotaZac,$%x,%d,0\\n\",PA-&gt;Kanal,PA-&gt;Ton);<br>}<br>#endif<br>}<br>VypustBCas(&amp;PI-&gt;FrontaAK,&amp;PA-&gt;B);<br>PI-&gt;ObsKan--;<br>if( PA-&gt;back ) *PA-&gt;back=NULL;<br>PA-&gt;back=NULL;<br>}<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err ZapKan( PreklInfo *PI, AlokKan *PA, int LogTon, int Vol, Flag Legato, CasT Cas )<br>{<br>Err ret;<br>while( LogTon&lt;0 ) LogTon+=12; while( LogTon&gt;127 ) LogTon-=12;<br>Vol&gt;&gt;=1;<br>if( Vol&gt;127 ) Vol=127;<br>if( Vol&lt;1 ) Vol=1; ret=NutnoSynchro(PI);if( retCekamLegato )<br>{<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(MNotaZac&lt;&lt;8)|PA-&gt;Kanal|0x40) ) return ERAM;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pNotaZac,$%x,\",PA-&gt;Kanal+0x40);<br>}<br>#endif<br>}<br>else<br>{<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(MNotaZac&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pNotaZac,$%x,\",PA-&gt;Kanal);<br>}<br>#endif<br>}<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(LogTon&lt;&lt;8)|Vol) ) return ERAM; #if PRO_JAG if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"%d,%d\\n\",LogTon,Vol);<br>}<br>#endif<br>PI-&gt;NejakyTon=True;<br>PA-&gt;Ton=LogTon;<br>PA-&gt;FadeOut=False;<br>if( Legato ) PA-&gt;CekamLegato=True;<br>else PA-&gt;CekamLegato=False;<br>if( !PA-&gt;Rytm )<br>{<br>ZaradBCas(&amp;PI-&gt;FrontaAK,&amp;PA-&gt;B,Cas);<br>PI-&gt;ObsKan++;<br>if( PI-&gt;MaxObsKanObsKan ) PI-&gt;MaxObsKan=PI-&gt;ObsKan;<br>}<br>else<br>{ /* Rytmika nazabira cas <em>/ /</em> nepocitame ji ani do kanalu - neobsazuje */<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(MNotaZac&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(LogTon&lt;&lt;8)|0) ) return ERAM; #if PRO_JAG if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pNotaZac,%d,%d,0\\n\",PA-&gt;Kanal,LogTon);<br>}<br>#endif<br>PA-&gt;Ton=-1;<br>if( PA-&gt;back ) *PA-&gt;back=NULL;<br>PA-&gt;back=NULL;<br>}<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static char SBuf[256];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int AktDefHNastr( PreklInfo *PI, KanPrekl *K, const char <em>NN, int V, int Ton ) { int F=AktHraj-&gt;DefHNastr(NN,Ton,V,SekvenceS(&amp;K-&gt;p)-&gt;Pis); if( Fp,K-&gt;Kan,PI,SBuf); return ERR; } ef( F==EFIL ) { ComChyba(&amp;K-&gt;p,K-&gt;Kan,PI,FreeString(IOERRA)); return ERR; } ef( F==KON ) { if( !PI-&gt;IgnorujNDef &amp;&amp; 1!=AlertRF(CENDNASA,1,NN) ) { ComChyba(&amp;K-&gt;p,K-&gt;Kan,PI,FreeString(IOERRA)); return ECAN; } PI-&gt;IgnorujNDef=True; return -1; /</em> pauza nastroj! */<br>}<br>}<br>return F;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err SetNastr( PreklInfo *PI, AlokKan *PA, KanPrekl <em>K, int V, int Ton ) { /</em> V je cislovani v pisni <em>/ /</em> musime nechat prehravac, at si ho ocisluje sam */<br>const Pisen *P=SekvenceS(&amp;PI-&gt;KP[0].p)-&gt;Pis;<br>const char <em>NN; if( PA-&gt;LNastroj!=V ) { Err ret; int F; Plati( V&gt;=0 ); NN=ZBanky(P-&gt;Nastroje,V); F=AktDefHNastr(PI,K,NN,V,Ton); /</em> V pomaha hledat */<br>if( F&lt;0 ) return F; ret=NutnoSkoroSynchro(PI);if( retPisBuf,(MNastr&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>if( !ZapisW(&amp;PI-&gt;PisBuf,F) ) return ERAM;<br>#if PRO_JAG<br>if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.w (pNastr&lt;&lt;8)|%d,ins_%s\\n\",PA-&gt;Kanal,NN);<br>}<br>#endif<br>PA-&gt;LNastroj=V;<br>PA-&gt;FNastroj=F;<br>}<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err SetEfekt( PreklInfo *PI, AlokKan *PA, int EA, int EB )<br>{<br>if( PA-&gt;EffA!=EA || PA-&gt;EffB!=EB )<br>{<br>Err ret;<br>PA-&gt;EffA=EA,PA-&gt;EffB=EB;<br>if( EA&lt;0 ) EA=0;ef( EA&gt;+255 ) EA=+255;<br>if( EB&lt;0 ) EB=0;ef( EB&gt;+255 ) EB=+255;<br>ret=NutnoSkoroSynchro(PI);if( retPisBuf,(MEfekt&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(EA&lt;&lt;8)|(byte)EB) ) return ERAM; #if PRO_JAG if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pEfekt,%d,%d,%d\\n\",PA-&gt;Kanal,EA,EB);<br>}<br>#endif<br>}<br>return EOK;<br>}<br>static Err SetStereo( PreklInfo *PI, AlokKan *PA, int VMin, int VMax )<br>{<br>if( PA-&gt;StereoMin!=VMin || PA-&gt;StereoMax!=VMax )<br>{<br>Err ret;<br>PA-&gt;StereoMin=VMin,PA-&gt;StereoMax=VMax;<br>if( VMin&lt;-127 ) VMin=-127;ef( VMin&gt;+127 ) VMin=+127;<br>if( VMax&lt;-127 ) VMax=-127;ef( VMax&gt;+127 ) VMax=+127;<br>ret=NutnoSkoroSynchro(PI);if( retPisBuf,(MStereo&lt;&lt;8)|PA-&gt;Kanal) ) return ERAM;<br>VMin=(byte)VMin;<br>VMax=(byte)VMax;<br>if( !ZapisW(&amp;PI-&gt;PisBuf,(VMin&lt;&lt;8)|(byte)VMax) ) return ERAM; #if PRO_JAG if( PI-&gt;GenJag )<br>{<br>fprintf(PI-&gt;GenJag,\"\\tdc.b pStereo,%d,%d,%d\\n\",PA-&gt;Kanal,VMin,VMax);<br>}<br>#endif<br>}<br>return EOK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define DEDENI 1</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static AlokKan *PridelKanal( PreklInfo *PI, KanPrekl *K, int Ton )<br>{<br>Kontext *AK=AKontextS(&amp;K-&gt;p);<br>if( AK-&gt;Nastroj&gt;=0 )<br>{<br>const Pisen *P=SekvenceS(&amp;PI-&gt;KP[0].p)-&gt;Pis;<br>const char *NN=ZBanky(P-&gt;Nastroje,AK-&gt;Nastroj);<br>int F=AktDefHNastr(PI,K,NN,AK-&gt;Nastroj,Ton);<br>AlokKan <em>PT,</em>PA,*PP;<br>if( FFrontaAK.Volne; PA; PA=(AlokKan *)PA-&gt;B.Dalsi )<br>{<br>if( PA-&gt;LNastroj==AK-&gt;Nastroj ) PP=PA;<br>if( PA-&gt;LKanal==K ) PT=PA;<br>}<br>#endif<br>if( !PT ) PT=PP; /* spokoj se i se stejnym nastrojem */<br>for( PA=PT ? PT : (AlokKan *)PI-&gt;FrontaAK.Volne; PA; PA=(AlokKan <em>)PA-&gt;B.Dalsi ) { /</em> pri PT uspeje rovnou <em>/ if( !PA-&gt;CekamLegato || PA-&gt;LKanal==K ) { /</em> legato se smi pridedit jen na stejny kanal <em>/ Err ret; if( !PT || AK-&gt;Nastroj!=PA-&gt;LNastroj ) PA-&gt;CekamLegato=False; ret=SetNastr(PI,PA,K,AK-&gt;Nastroj,Ton); if( retStereoMin,AK-&gt;StereoMax);if( retEffA,AK-&gt;EffB);if( retMaxObsKan=PI-&gt;NFyzKan+1; /</em> kanal se nenasel <em>/ return NULL; } return NULL; /</em> nepotrebuje kanal */<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void MazPosuv( PosuvT Pos, SekvKursor *s )<br>{<br>const static int PoradiK[7]={TF,TC,TG,TD,TA,TE,TH};<br>const static int PoradiB[7]={TH,TE,TA,TD,TG,TC,TF};<br>int i,T;<br>for( i=0; i&lt;7; i++ ) Pos[i]=TonN; T=s-&gt;A.Tonina;<br>if( T==-128 ) T=0;<br>if( T&gt;0 )<br>{<br>for( i=0; i&lt;T; i++ ) Pos[PoradiK[i]-TC]=TonK;<br>}<br>ef( T&lt;0 )<br>{<br>T=-T;<br>for( i=0; i&lt;T; i++ ) Pos[PoradiB[i]-TC]=TonB;<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">if FADE_OUT</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define CasFade (sec/200) /* 1/1000s je 25 vzorku DMA */</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static long FadeOuty( PreklInfo <em>PI, long Cas ) { /</em> proved ty FO, ktere se stihnou v case &lt;=Cas */ Err ret; Budik *B; long PBK=0; long CZap=0; for( B=PrvniBud(&amp;PI-&gt;FrontaAK); B; B=DalsiBud(B) )<br>{<br>long C;<br>AlokKan *AK=(AlokKan *)B;<br>PBK+=B-&gt;ZaDobu;<br>C=PBK-CasFade;<br>Plati( AK-&gt;Ton&gt;=0 );<br>if( !AK-&gt;FadeOut &amp;&amp; !AK-&gt;CekamLegato &amp;&amp; C&lt;=Cas &amp;&amp; C&gt;=0 )<br>{<br>ret=ZapisCekani(PI,C);if( ret&lt;EOK ) return ret;<br>PBK-=C;<br>Cas-=C;<br>CZap+=C;<br>ret=FOutKan(PI,AK);if( ret&lt;EOK ) return ret;<br>}<br>}<br>return CZap;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static CasT DelkaZneni( Pisen *P, int Nas, int Ton )<br>{<br>if( AktHraj-&gt;DelkaZneni ) return AktHraj-&gt;DelkaZneni(P,Nas,Ton);<br>return MAXCAS;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err ZpracujKanal( KanPrekl *K, PreklInfo *PI, Flag PlneRep )<br>{<br>Err ret;<br>long Krok=K-&gt;B.ZaDobu;<br>Budik <em>B; CasT Del=0; PI-&gt;DosCas+=Krok; { /</em> simulace prechodu na nejblizsi udalost - NoteOffy, FadeOuty <em>/ long PBK=0; B=PrvniBud(&amp;PI-&gt;FrontaAK); while( B ) { /</em> davno dohrane tony muzeme zrusit - prave dohrane si jeste nechame <em>/ PBK+=B-&gt;ZaDobu; if( PBK&lt;=Krok ) { /</em> stihne dohrat - NoteOff */<br>Budik *Nxt;<br>#if FADE_OUT<br>{<br>long C=FadeOuty(PI,PBK);<br>if( CZaDobu se take srazi na nulu - VypKan */<br>Nxt=DalsiBud(B);<br>ret=VypKan(PI,(AlokKan *)B);if( retZaDobu&gt;=Krok */<br>}<br>}<br>#if FADE_OUT<br>{<br>l