r>ret=VypKan(PI,(AlokKan *)PrvniBud(AK));<br>}<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void ZacPreklad( Sekvence *S, CasT CasOd, Kontext *K, PreklInfo *PI )<br>{<br>int Kan;<br>Sekvence *H;<br>CasT MinCas=MAXCAS;<br>Plati( K );<br>for( Kan=0,H=S; H &amp;&amp; KanKP[Kan];<br>CasT c;<br>OtevriS(&amp;KS-&gt;p,H,Cteni);<br>SetKKontext(AktK(&amp;KS-&gt;p),K);<br>RezimS(&amp;KS-&gt;p,True);<br>NajdiSCasS(&amp;KS-&gt;p,CasOd);<br>c=CasJeS(&amp;KS-&gt;p);<br>NajdiCasS(&amp;KS-&gt;p,c);<br>if( TestS(&amp;KS-&gt;p)==EOK ) if( cPos,AktK(&amp;KS-&gt;p));<br>KS-&gt;Kan=Kan;<br>For( AlokKan **, pa, KS-&gt;FyzK, &amp;KS-&gt;FyzK[NAkTon], pa++ )<br>*pa=NULL;<br>Next<br>KS-&gt;RepUr=0;<br>}<br>PI-&gt;NKan=Kan;<br>Plati( PI-&gt;NKan&gt;0 );<br>PI-&gt;FrontaKP.Prvni=NULL;<br>PI-&gt;FrontaKP.Volne=&amp;PI-&gt;KP-&gt;B;<br>PI-&gt;ZbyvaSynchro=0;<br>PI-&gt;ObsKan=PI-&gt;MaxObsKan=0;<br>for( Kan=0; KanNKan-1; Kan++ )<br>{<br>KanPrekl *KS=&amp;PI-&gt;KP[Kan];<br>KS-&gt;B.Dalsi=&amp;KS[1].B;<br>}<br>PI-&gt;KP[Kan].B.Dalsi=NULL;<br>for( Kan=0; KanNKan; Kan++ )<br>{<br>KanPrekl *KS=&amp;PI-&gt;KP[Kan];<br>ZaradBCas(&amp;PI-&gt;FrontaKP,&amp;KS-&gt;B,CasJeS(&amp;KS-&gt;p)-MinCas);<br>}<br>PI-&gt;FrontaAK.Prvni=NULL;<br>PI-&gt;FrontaAK.Volne=&amp;PI-&gt;KA-&gt;B;<br>for( Kan=0; KanNFyzKan-1; Kan++ )<br>{<br>AlokKan *AK=&amp;PI-&gt;KA[Kan];<br>AK-&gt;B.Dalsi=&amp;AK[1].B;<br>}<br>PI-&gt;KA[Kan].B.Dalsi=NULL;<br>for( Kan=0; KanNFyzKan; Kan++ )<br>{<br>AlokKan *AK=&amp;PI-&gt;KA[Kan];<br>AK-&gt;Kanal=Kan;<br>AK-&gt;Ton=-1;<br>AK-&gt;LNastroj=-1;<br>AK-&gt;FNastroj=-1;<br>AK-&gt;LKanal=NULL;<br>AK-&gt;PBender=0;<br>AK-&gt;StereoMin=AK-&gt;StereoMax=-0x7fff;<br>AK-&gt;EffA=AK-&gt;EffB=-1; /* nenЎ nastaveno */<br>AK-&gt;CekamLegato=False;<br>}<br>PI-&gt;DosCas=MinCas;<br>PI-&gt;IgnorujNDef=False;<br>PI-&gt;NejakyTon=False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void KonPreklad( PreklInfo *PI )<br>{<br>int Kan;<br>for( Kan=0; KanNKan; Kan++ )<br>{<br>KanPrekl *KS=&amp;PI-&gt;KP[Kan];<br>ZavriS(&amp;KS-&gt;p);<br>}<br>ZrusBud(&amp;PI-&gt;FrontaKP);<br>ZrusBud(&amp;PI-&gt;FrontaAK);<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Err Preloz( Sekvence *S, CasT CasOd, Kontext *K, PreklInfo <em>PI ) { Err ret=EOK; ZacPreklad(S,CasOd,K,PI); /</em> inic. kanalu <em>/ /</em> prekladaci smycka */<br>while( PrvniBud(&amp;PI-&gt;FrontaKP) &amp;&amp; ret&gt;=EOK )<br>{<br>ret=ZpracujKanal((KanPrekl *)PrvniBud(&amp;PI-&gt;FrontaKP),PI,CasOd&lt;=0);<br>} /* while( je povel ) <em>/ if( ret&gt;=EOK ) ret=UkonciTony(PI); if( ret&gt;=EOK ) { ret=NutnoSynchro(PI);if( retPisBuf,(MKonec&lt;&lt;8)|0) ) ret=ERAM; if( !ZapisW(&amp;PI-&gt;PisBuf,0) ) ret=ERAM; #if PRO_JAG if( PI-&gt;GenJag ) { fputc(pKonec,PI-&gt;GenJag); fputc(0,PI-&gt;GenJag); fputc(0,PI-&gt;GenJag); fputc(0,PI-&gt;GenJag); } #endif } KonPreklad(PI); /</em> ruseni kanalu */<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static PreklInfo PInfo;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag ComLock=False;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void ZrusComLock( void )<br>{<br>ComLock=False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void ComDohraj( void )<br>{<br>PustBuf(&amp;PInfo.PisBuf);<br>AktHraj-&gt;KonHNastr();<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>PathName CocPath;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Err CompoCom( Flag Hraj, Sekvence *S, CasT CasOd, Kontext *Kont, CompoErr <em>CE ) { CE-&gt;Ukazat=False; if( AktHraj ) { Err p; if( ComLock ) return ECAN; ComLock=True; Mouse(HOURGLASS); PovolCinMenu(False); p=AktHraj-&gt;ZacHNastr(S-&gt;Pis); if( p&gt;=OK ) { PathName C; PInfo.CErr=CE; PInfo.S=S; InitBuf(&amp;PInfo.PisBuf); PInfo.NFyzKan=AktHraj-&gt;NFyzKan; PInfo.GenJag=NULL; #if PRO_JAG if( !Hraj ) { PathName CP,CN; FileName N; strcpy(C,S-&gt;Pis-&gt;Cesta); strcpy(N,NajdiNazev(C)); strcpy(NajdiPExt(N),\"\"); strcpy(NajdiPExt(NajdiNazev(C)),\".BIN\"); VyrobCesty(CP,CN,C); if( !</em>CocPath ) strcpy(CocPath,CP);<br>else strcpy(CP,CocPath);<br>if( FSel(CocPath,CN,C) )<br>{<br>PInfo.GenJag=fopen(C,\"wb\");<br>LoadNas(CocPath);<br>}<br>FAktHNastr();<br>}<br>#endif<br>p=Preloz(S,CasOd,Kont,&amp;PInfo);<br>if( p&gt;=OK &amp;&amp; PInfo.NejakyTon )<br>{<br>AktHraj-&gt;AktHNastr();<br>if( Hraj ) p=Zahraj(NazevSekvPis(S),PInfo.PisBuf.Buf,PInfo.DosCas,CasOd,PInfo.MaxObsKan,S-&gt;Pis);<br>else<br>{<br>#if !PRO_JAG<br>PathName CP,CN;<br>/* pisen je v PInfo.PisBuf <em>/ /</em> tabulku nastroju si drzi AktHraj <em>/ strcpy(C,S-&gt;Pis-&gt;Cesta); strcpy(NajdiExt(NajdiNazev(C)),\"COC\"); VyrobCesty(CP,CN,C); if( !</em>CocPath ) strcpy(CocPath,CP);<br>else strcpy(CP,CocPath);<br>if( FSel(CocPath,CN,C) )<br>{<br>FILE <em>f=fopen(C,\"wb\"); Err ret=EFIL; if( f ) { long c; if( setvbuf(f,NULL,_IOFBF,9</em>1024)&lt;0 ) goto Error; c=PInfo.PisBuf.Ptr; if( !AktHraj-&gt;SaveHNastr || AktHraj-&gt;SaveHNastr(f,S-&gt;Pis,C)SaveHNastr || AktHraj-&gt;SaveHNastr(PInfo.GenJag,S-&gt;Pis,C)&lt;EOK ) {}<br>fclose(PInfo.GenJag);<br>}<br>#endif<br>}<br>}<br>AzDohraje(ComDohraj);<br>}<br>AzDohraje(ZrusComLock);<br>PovolCinMenu(True);<br>return p;<br>}<br>else return OK;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->', 'COMCOMPO.cpp', '', 'inherit', 'closed', 'closed', '', '82-revision-v1', '', '', '2023-05-13 13:42:51', '2023-05-13 13:42:51', '', 82, 'http://xboxcode.hstn.me/?p=83', 0, 'revision', '', 0),
(84, 1, '2023-05-13 13:43:24', '2023-05-13 13:43:24', '<!-- wp:paragraph -->\n<p>/*<br>spolecne deklarace pro AY-digitalni prekladac a player<br>SUMA 1993<br>*/</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum<br>{<br>/* povely hraciho strojku <em>/ MNotaZac=0,MSync, /</em> NotaKon je Zac s volume 0 <em>/ MNastr,MStereo, /</em> kontrolery <em>/ MKonec, /</em> dalзЎ povely pэid&nbsp;v&nbsp;me na konec - chceme z…stat kompatibilnЎ <em>/ MEfekt, }; enum { MaxLogKan=32, Okt0Ind=3, /</em> vzdalenost oktavy 0 od tonu C2 <em>/ OktMaxInd=8, /</em> vzdalenost uz nezvl. oktavy od tonu C2 */<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>typedef signed char MPovel;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* PLYCOMPO.C */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">ifdef __COMPOUTL</h1>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>Err Zahraj( const char *Naz, const MPovel *Pis, CasT Del, CasT Od, int MaxKan, Pisen *P );</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>/* import */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void RecalcFreq( double Freq ); /* pэepoџЎtej vnitэnЎ buffery */</p>\n<!-- /wp:paragraph -->', 'COMCOMPO.H', '', 'publish', 'open', 'open', '', 'comcompo-h', '', '', '2023-05-13 13:43:25', '2023-05-13 13:43:25', '', 0, 'http://xboxcode.hstn.me/?p=84', 0, 'post', '', 0),
(85, 1, '2023-05-13 13:43:24', '2023-05-13 13:43:24', '<!-- wp:paragraph -->\n<p>/*<br>spolecne deklarace pro AY-digitalni prekladac a player<br>SUMA 1993<br>*/</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum<br>{<br>/* povely hraciho strojku <em>/ MNotaZac=0,MSync, /</em> NotaKon je Zac s volume 0 <em>/ MNastr,MStereo, /</em> kontrolery <em>/ MKonec, /</em> dalзЎ povely pэid&nbsp;v&nbsp;me na konec - chceme z…stat kompatibilnЎ <em>/ MEfekt, }; enum { MaxLogKan=32, Okt0Ind=3, /</em> vzdalenost oktavy 0 od tonu C2 <em>/ OktMaxInd=8, /</em> vzdalenost uz nezvl. oktavy od tonu C2 */<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>typedef signed char MPovel;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* PLYCOMPO.C */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">ifdef __COMPOUTL</h1>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>Err Zahraj( const char *Naz, const MPovel *Pis, CasT Del, CasT Od, int MaxKan, Pisen *P );</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>/* import */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void RecalcFreq( double Freq ); /* pэepoџЎtej vnitэnЎ buffery */</p>\n<!-- /wp:paragraph -->', 'COMCOMPO.H', '', 'inherit', 'closed', 'closed', '', '84-revision-v1', '', '', '2023-05-13 13:43:24', '2023-05-13 13:43:24', '', 84, 'http://xboxcode.hstn.me/?p=85', 0, 'revision', '', 0),
(86, 1, '2023-05-13 13:44:09', '2023-05-13 13:44:09', '<!-- wp:paragraph -->\n<p>; COMPO.ASM<br>; spojovani kanalu + stereofonie + efekty pro Componium<br>; SUMA 9/1995-9/1995<br>; komunikuje pэes host-port</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>nolist</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>compo ident 1,0<br>include \"intequ.asm\"<br>include \"ioequ.asm\"<br>include \"macros.asm\"<br>list</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; externe musi byt definovan NKan bud jako 16, 8, 4, nebo jako 2<br>; ex. r…zn‚ varianty LODu</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; CRA bits------------------------------------------------<br>; 15 = PS = 0 : 0=/1, 1=/8, prescalar<br>; 14-13 = WL1-0 = 10 : 00=8, 01=12, 10=16, 11=24 bits (set to 16-bits now)<br>; 12-8 = DC4-DC0 = 0 0001 : divider control (normally word transfer rate)<br>; 7-0 = PM7-0 = $00 : divide by 1-256, 0=fastest (divide by 1)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRA_BITS EQU $4100 ; ve skut. 4 kanaly (data+stereo)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; SSI CRB bits --------------------------------------------------<br>; CRB determines the serial modes<br>; 15 = RIE = 1 : 0=disable, 1=enable receive interrupt<br>; 14 = TIE = 1 : 0=disable, 1=enable transmit interrupt<br>; 13 = RE = 1 : 0=disable, 1=enable receive<br>; 12 = TE = 1 : 0=disable, 1=enable transmit</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 11 = MODE = 1 : 0=normal, 1=network<br>; 10 = GCK = 0 : 0=continuous, 1=gated<br>; 9 = SYN = 0 : 0=asynchronous, 1=synchronous, transmit and receive<br>; 8 = FSL0 = 0 : 0=word, 1=bit length, Frame Sync clock</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 7 = FSL1 = 0 : 0=TX and RX same length, 1=different<br>; 6 = SHFD = 0 : 0=MSB first, 1=LSB first<br>; 5 = SCKD = 0 : 0=external, 1=internal bit clock<br>; 4 = SCD2 = 0 : 0=input, 1=output on SC2, transmit frame sync</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 3 = SCD1 = 0 : 0=input, 1=output on SC1, receive frame sync<br>; 2 = SCD0 = 0 : 0=input, 1=output on SC0, receive clock<br>; 1 = OF1 = 0 :<br>; 0 = OF0 = 0 : OF0 not to SC0 ; must match SCKD</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRB_BITS EQU $5800</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; TOS 4.04 nekdy spatne inicializuje L: promenne umistene v interni pameti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org l:0</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>SavePR: ds 1<br>Volume: ds 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>KanVol: ds 1<br>FadeSpd: ds 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; sily efektu<br>swspc x<br>EffASila: ds 1<br>EffBSila: ds 1<br>EffBMap: ds 1 ; indikuje dlouhodob‚ pou§itЎ efekt…</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; parametry pro echo<br>swspc l</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dsp_init_zac equ *</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>eff_a_par: ds 8<br>eff_b_par: ds 8</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dsp_init_kon equ *</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>swspc x</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>EffParTab: dc eff_a_par,eff_b_par</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>FBRamPomer equ 128</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; buffery, do kterмch sџЎt&nbsp;me data<br>eff_a_sum: dsm FBRamPomer<br>eff_b_sum: dsm FBRamPomer</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; pracovnЎ buffery pro echo<br>EffLenBuf equ $1000<br>eff_a_buf: dsm EffLenBuf<br>eff_b_buf: dsm EffLenBuf</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>DSPLenBuf equ $1000-$200</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>out_buffer: dsm DSPLenBuf</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org p:$0\njmp start\n\norg p:SSITD\njsr dataout\norg p:SSITDE\njsr dataout\n\norg p:$40</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>; R1(M1) je ukazatel pro z&nbsp;pis do bufferu out_bufferu<br>; R2(M2) je ukazatel pro џtenЎ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dataout:<br>jset #TFS,X:&lt;&lt;SSR,_dout_snc1<br>movep X:(R2),X:&lt;&lt;TX<br>rti<br>_dout_snc1:<br>movep Y:(R2)+,X:&lt;&lt;TX<br>rti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>start:<br>movep #$1F8,X:&lt;&lt;PCC<br>movep #0,X:&lt;&lt;BCR<br>movep #1,X:PBC<br>move SR,Y:SavePR<br>andi #&lt;$F0,MR ; scaling mode na 0, interrupt na 0<br>movep #CRA_BITS,X:&lt;&lt;CRA<br>; priprav datove oblasti<br>; cele prijimane bloky - vc. sterea</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>move #0.8,Y0 ; nech malou rezervu\nmove Y0,Y1\nmove #0,X0\nmove X0,X1\nmove Y0,X:Volume\nmove X0,Y:Volume\n\nmove #out_buffer,R1\nmove #DSPLenBuf-1,M1\nmove R1,R2\nmove M1,M2\nmove #&gt;DSPLenBuf,Y0\nrep Y0 ; vymaz a inicializuj vyrovnavaci buffer\nmove X,L:(R1)+\n\nmove #dsp_init_zac,R0\nrep #dsp_init_kon-dsp_init_zac\nmove X,L:(R0)+\n\nmove #-1,M5\nmove #&gt;EffLenBuf,Y0\nmove #eff_a_buf,R5\nrep Y0\nmove X,L:(R5)+\nmove #eff_b_buf,R5\nrep Y0\nmove X,L:(R5)+\n\n; inicialzuj efekty\n; default hodnoty A - long delay\nmove #&gt;EffLenBuf-1,Y1\nmove #&gt;eff_a_buf,Y0\n;move Y,L:eff_a_par\nmove Y1,X:eff_a_par\nmove Y0,Y:eff_a_par\nmove #0.7,Y0\nmove Y0,X:eff_a_par+1 ; feedback\nmove #0.9,Y0\nmove Y0,X:eff_a_par+2 ; gain\n\n; default hodnoty B - short delay\nmove #&gt;EffLenBuf/3-1,Y1\nmove #&gt;eff_b_buf,Y0\nmove Y1,X:eff_b_par\nmove Y0,Y:eff_b_par\nmove #0.6,Y0\nmove Y0,X:eff_b_par+1 ; feedback\nmove #0.9,Y0\nmove Y0,X:eff_b_par+2 ; gain\n\nmove #0,X0\nmove X0,X:EffBMap</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>;read SSI SR to clear TUE bit<br>btst #4,X:&lt;&lt;SSR<br>movep #CRB_BITS,X:&lt;&lt;CRB<br>; interrupt priority<br>movep X:&lt;&lt;IPR,X:SavePR<br>movep #$3000,X:&lt;&lt;IPR</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>main:<br>; tady muzes posilat maxima (na pozadani)<br>;cekame na dva bloky dat<br>_rcvcom: jclr #0,X:&lt;&lt;HSR,_rcvcom<br>movep X:&lt;&lt;HRX,X0<br>jset #0,X0,ComAbort<br>jset #1,X0,ComGetPos<br>jset #2,X0,ComPutData<br>jset #3,X0,ComFadeOut<br>jset #4,X0,ComVolume<br>jset #5,X0,ComEffPar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComAbort:<br>movep #0,X:&lt;&lt;CRB ; disable interr.<br>movep X:SavePR,X:&lt;&lt;IPR ; vrat IPR<br>move Y:SavePR,SR<br>DummyLop:<br>_Wait: jclr #0,X:&lt;&lt;HSR,_Wait<br>movep X:&lt;&lt;HRX,X0 ; prevezmi vyp.<br>jmp DummyLop</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComGetPos:<br>; zjisti n&nbsp;skok џtecЎ hlavy pэed zapisovacЎ (voln‚ mЎsto)<br>move R2,A<br>move R1,X0<br>sub X0,A #&gt;DSPLenBuf,X0<br>jge &lt;_plus<br>add X0,A<br>_plus:<br>_sndpos: jclr #1,X:&lt;&lt;HSR,_sndpos<br>movep A,X:&lt;&lt;HTX<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComPutData:<br>; pэevezme data pro jeden blok zvuku<br>_rcvnkan: jclr #0,X:&lt;&lt;HSR,_rcvnkan<br>movep X:&lt;&lt;HRX,N7</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>move R1,R0\nmove M1,M0\n\nmove #eff_a_sum,R3\nmove #eff_b_sum,R4\nmove #FBRamPomer-1,M3\nmove M3,M4\n\nclr B\ndo #FBRamPomer,_ClrLop\n    move B,L:(R0)+\n    move B,L:(R3)+\n    move B,L:(R4)+\n_ClrLop:\n\ndo N7,_KanLop\n    _rcvbal: jclr #0,X:&lt;&lt;HSR,_rcvbal\n    movep X:&lt;&lt;HRX,Y0\n\n    move #0,N0\n\n    ; neprve fade (zhora)\n    tfr Y0,B #&gt;$ff0000,X1\n    tst B\n    jeq &lt;_Nezni\n\n    and X1,B1 #&gt;1&lt;&lt;(23-6),X1\n\n    ; je to 7b, ale o 6b posunuto nahoru\n    jeq &lt;_NoFade\n        move B1,X0\n        mpy X1,X0,B ; m&amp;nbsp;me rychlost fadeoutu rel. k celk. hlasitosti\n        bset #0,N0\n        move B1,X:FadeSpd\n    _NoFade:\n\n    tfr Y0,B #&gt;$ff,X0\n    and X0,B1 #&gt;1&lt;&lt;(23-8),X1\n    mpy X1,Y0,A B1,Y0\n    and X0,A1 #&gt;1&lt;&lt;(23-9),X1\n    ; Y0 a A1 obsahujЎ 8b balance\n    ; posuneme-li je o 9b dol…, dostaneme v A0,B0 23 balance\n    mpy Y0,X1,B A1,Y0\n    mpy Y0,X1,A\n\n    move A0,X1\n    move B0,X0\n    move X,L:KanVol\n\n    jclr #0,N0,_nocfade\n        move X:FadeSpd,Y0\n        ;move #0.01,Y0\n        mpy Y0,X1,A\n        mpy Y0,X0,B\n        move AB,L:FadeSpd\n    _nocfade:\n\n    _rcveff: jclr #0,X:&lt;&lt;HSR,_rcveff\n    movep X:&lt;&lt;HRX,Y0\n\n    move #0,N1 ; zaznamen&amp;nbsp;v&amp;nbsp;me pou§itЎ effekt…\n\n    tfr Y0,B #&gt;$ff,X0\n    and X0,B1 #&gt;1&lt;&lt;(23-8),X1\n    jeq &lt;_NoEffB\n        bset #1,N1\n        bset #1,X:EffBMap\n    _NoEffB:\n    mpy X1,Y0,A B1,Y0\n    and X0,A1 #&gt;1&lt;&lt;(23-9),X1\n    jeq &lt;_NoEffA\n        bset #0,N1\n        bset #0,X:EffBMap\n    _NoEffA:\n    ; Y0 a A1 obsahujЎ 8b balance\n    ; posuneme-li je o 9b dol…, dostaneme v A0,B0 23b sЎlu efektu\n    mpy Y0,X1,B A1,Y0\n    mpy Y0,X1,A\n\n    move A0,X:EffASila\n    move B0,X:EffBSila\n\n    move R1,R0\n    do #FBRamPomer,_AddLop\n        _rcvdata: jclr #0,X:&lt;&lt;HSR,_rcvdata\n        movep X:&lt;&lt;HRX,X0\n\n        ;move #&gt;(1&lt;&lt;12),X0\n\n        move L:KanVol,Y ; balance (frac)\n        mpy X0,Y1,A ; X0 je 16 bit… se znam‚nkem\n        mpy X0,Y0,B\n\n        move A,X1\n        move B,X0\n\n        ; v X m&amp;nbsp;me data pro zpracov&amp;nbsp;nЎ pэes efekty\n\n        ; pэiџЎt&amp;nbsp;me k hlavnЎmu bufferu\n        move L:(R0),AB\n        add X1,A\n        add X0,B\n        move AB,L:(R0)+\n\n        jclr #0,N1,_NoSumA\n            move X:EffASila,Y0\n            move L:(R3),AB\n            mac Y0,X1,A\n            mac Y0,X0,B\n            move AB,L:(R3)+\n        _NoSumA:\n        jclr #1,N1,_NoSumB\n            move X:EffBSila,Y0\n            move L:(R4),AB\n            mac Y0,X1,A\n            mac Y0,X0,B\n            move AB,L:(R4)+\n        _NoSumB:\n\n        jclr #0,N0,_NoIFade\n            move L:KanVol,AB\n            move L:FadeSpd,X\n            sub X1,A #0,Y1\n            tlt Y1,A\n            sub X0,B\n            tlt Y1,B\n            move AB,L:KanVol\n        _NoIFade:\n        nop\n\n    _AddLop:\n    _Nezni:\n    nop\n\n_KanLop:\n\n; spoџЎtej efekty\n\njclr #0,X:EffBMap,_NoATot\n    move #eff_a_sum,R4\n    move #eff_a_par,R5\n    move R1,R0\n    jsr &lt;EffEcho\n_NoATot:\n\njclr #1,X:EffBMap,_NoBTot\n    move #eff_b_sum,R4\n    move #eff_b_par,R5\n    move R1,R0\n    jsr &lt;EffEcho\n_NoBTot:\n\nmove R1,R0\ndo #FBRamPomer,_SatLop\n    move L:Volume,Y ; celkovм fade-out\n    clr B Y1,A\n    sub Y0,A L:(R0),X\n    tlt B,A\n    mpy X0,Y1,B A,X:Volume\n    mpy X1,Y1,A\n    rep #8\n    asl A\n    rep #8\n    asl B\n    move AB,L:(R0)+\n_SatLop:\n\nmove R0,R1 ; pэejdi na dalзЎ Јsek\n\njmp &lt;main</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>ComFadeOut:<br>_WaitL: jclr #0,X:&lt;&lt;HSR,_WaitL<br>movep X:&lt;&lt;HRX,Y:Volume<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComVolume:<br>_WaitL: jclr #0,X:&lt;&lt;HSR,_WaitL<br>movep X:&lt;&lt;HRX,X:Volume<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComEffPar:<br>move #-1,M0<br>move #EffParTab,R0<br>_rcvnum: jclr #0,X:&lt;EffLenBuf-1,X1<br>mpy X0,X1,A<br>cmp X1,A<br>tgt X1,A<br>move A,X:(R0)<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; efekty:<br>; v R4 dost&nbsp;v&nbsp; vstup<br>; v R5 dost&nbsp;v&nbsp; parametry<br>; do R0 m&nbsp; pэiџЎst vмstup</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; X:(R5) je delay-1 (tj. pэipraven pro M5)<br>; Y:(R5) je aktu&nbsp;lnЎ posice<br>; X:(R5)+1 je feedback<br>; X:(R5)+2 je gain</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>EffEcho:<br>; vezmi si parametry<br>move X:(R5),M3<br>move Y:(R5)+,R3<br>move X:(R5)+,Y0 ; feedback<br>move X:(R5)-,Y1 ; gain<br>move (R5)-</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>; proveФ efekt z R4 do R0\ndo #FBRamPomer,_echo_loop\n\n    ; vezmi si, co tebe zbylo z minula\n    move L:(R3),X\n\n    ; pэidej k tomu feedbackem novм vzorek\n    move L:(R4)+,AB\n    mac X1,Y0,A\n    mac X0,Y0,B\n    move AB,L:(R3)+\n\n    ; minulм vzorek pэidej podle gain do vмstupnЎho bufferu\n    move L:(R0),AB\n    mac X1,Y1,A\n    mac X0,Y1,B\n    move AB,L:(R0)+\n\n_echo_loop:\n\nmove R3,Y:(R5)\nrts</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>EndProg equ *<br>if @CVS(N,EndProg)&gt;$200<br>error \"Program not in internal RAM\"<br>endif</p>\n<!-- /wp:paragraph -->', 'COMPO.ASM', '', 'publish', 'open', 'open', '', 'compo-asm', '', '', '2023-05-13 13:44:11', '2023-05-13 13:44:11', '', 0, 'http://xboxcode.hstn.me/?p=86', 0, 'post', '', 0);
INSERT INTO `wpgl_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(87, 1, '2023-05-13 13:44:09', '2023-05-13 13:44:09', '<!-- wp:paragraph -->\n<p>; COMPO.ASM<br>; spojovani kanalu + stereofonie + efekty pro Componium<br>; SUMA 9/1995-9/1995<br>; komunikuje pэes host-port</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>nolist</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>compo ident 1,0<br>include \"intequ.asm\"<br>include \"ioequ.asm\"<br>include \"macros.asm\"<br>list</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; externe musi byt definovan NKan bud jako 16, 8, 4, nebo jako 2<br>; ex. r…zn‚ varianty LODu</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; CRA bits------------------------------------------------<br>; 15 = PS = 0 : 0=/1, 1=/8, prescalar<br>; 14-13 = WL1-0 = 10 : 00=8, 01=12, 10=16, 11=24 bits (set to 16-bits now)<br>; 12-8 = DC4-DC0 = 0 0001 : divider control (normally word transfer rate)<br>; 7-0 = PM7-0 = $00 : divide by 1-256, 0=fastest (divide by 1)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRA_BITS EQU $4100 ; ve skut. 4 kanaly (data+stereo)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; SSI CRB bits --------------------------------------------------<br>; CRB determines the serial modes<br>; 15 = RIE = 1 : 0=disable, 1=enable receive interrupt<br>; 14 = TIE = 1 : 0=disable, 1=enable transmit interrupt<br>; 13 = RE = 1 : 0=disable, 1=enable receive<br>; 12 = TE = 1 : 0=disable, 1=enable transmit</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 11 = MODE = 1 : 0=normal, 1=network<br>; 10 = GCK = 0 : 0=continuous, 1=gated<br>; 9 = SYN = 0 : 0=asynchronous, 1=synchronous, transmit and receive<br>; 8 = FSL0 = 0 : 0=word, 1=bit length, Frame Sync clock</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 7 = FSL1 = 0 : 0=TX and RX same length, 1=different<br>; 6 = SHFD = 0 : 0=MSB first, 1=LSB first<br>; 5 = SCKD = 0 : 0=external, 1=internal bit clock<br>; 4 = SCD2 = 0 : 0=input, 1=output on SC2, transmit frame sync</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 3 = SCD1 = 0 : 0=input, 1=output on SC1, receive frame sync<br>; 2 = SCD0 = 0 : 0=input, 1=output on SC0, receive clock<br>; 1 = OF1 = 0 :<br>; 0 = OF0 = 0 : OF0 not to SC0 ; must match SCKD</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRB_BITS EQU $5800</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; TOS 4.04 nekdy spatne inicializuje L: promenne umistene v interni pameti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org l:0</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>SavePR: ds 1<br>Volume: ds 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>KanVol: ds 1<br>FadeSpd: ds 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; sily efektu<br>swspc x<br>EffASila: ds 1<br>EffBSila: ds 1<br>EffBMap: ds 1 ; indikuje dlouhodob‚ pou§itЎ efekt…</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; parametry pro echo<br>swspc l</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dsp_init_zac equ *</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>eff_a_par: ds 8<br>eff_b_par: ds 8</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dsp_init_kon equ *</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>swspc x</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>EffParTab: dc eff_a_par,eff_b_par</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>FBRamPomer equ 128</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; buffery, do kterмch sџЎt&nbsp;me data<br>eff_a_sum: dsm FBRamPomer<br>eff_b_sum: dsm FBRamPomer</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; pracovnЎ buffery pro echo<br>EffLenBuf equ $1000<br>eff_a_buf: dsm EffLenBuf<br>eff_b_buf: dsm EffLenBuf</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>DSPLenBuf equ $1000-$200</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>out_buffer: dsm DSPLenBuf</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org p:$0\njmp start\n\norg p:SSITD\njsr dataout\norg p:SSITDE\njsr dataout\n\norg p:$40</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>; R1(M1) je ukazatel pro z&nbsp;pis do bufferu out_bufferu<br>; R2(M2) je ukazatel pro џtenЎ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dataout:<br>jset #TFS,X:&lt;&lt;SSR,_dout_snc1<br>movep X:(R2),X:&lt;&lt;TX<br>rti<br>_dout_snc1:<br>movep Y:(R2)+,X:&lt;&lt;TX<br>rti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>start:<br>movep #$1F8,X:&lt;&lt;PCC<br>movep #0,X:&lt;&lt;BCR<br>movep #1,X:PBC<br>move SR,Y:SavePR<br>andi #&lt;$F0,MR ; scaling mode na 0, interrupt na 0<br>movep #CRA_BITS,X:&lt;&lt;CRA<br>; priprav datove oblasti<br>; cele prijimane bloky - vc. sterea</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>move #0.8,Y0 ; nech malou rezervu\nmove Y0,Y1\nmove #0,X0\nmove X0,X1\nmove Y0,X:Volume\nmove X0,Y:Volume\n\nmove #out_buffer,R1\nmove #DSPLenBuf-1,M1\nmove R1,R2\nmove M1,M2\nmove #&gt;DSPLenBuf,Y0\nrep Y0 ; vymaz a inicializuj vyrovnavaci buffer\nmove X,L:(R1)+\n\nmove #dsp_init_zac,R0\nrep #dsp_init_kon-dsp_init_zac\nmove X,L:(R0)+\n\nmove #-1,M5\nmove #&gt;EffLenBuf,Y0\nmove #eff_a_buf,R5\nrep Y0\nmove X,L:(R5)+\nmove #eff_b_buf,R5\nrep Y0\nmove X,L:(R5)+\n\n; inicialzuj efekty\n; default hodnoty A - long delay\nmove #&gt;EffLenBuf-1,Y1\nmove #&gt;eff_a_buf,Y0\n;move Y,L:eff_a_par\nmove Y1,X:eff_a_par\nmove Y0,Y:eff_a_par\nmove #0.7,Y0\nmove Y0,X:eff_a_par+1 ; feedback\nmove #0.9,Y0\nmove Y0,X:eff_a_par+2 ; gain\n\n; default hodnoty B - short delay\nmove #&gt;EffLenBuf/3-1,Y1\nmove #&gt;eff_b_buf,Y0\nmove Y1,X:eff_b_par\nmove Y0,Y:eff_b_par\nmove #0.6,Y0\nmove Y0,X:eff_b_par+1 ; feedback\nmove #0.9,Y0\nmove Y0,X:eff_b_par+2 ; gain\n\nmove #0,X0\nmove X0,X:EffBMap</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>;read SSI SR to clear TUE bit<br>btst #4,X:&lt;&lt;SSR<br>movep #CRB_BITS,X:&lt;&lt;CRB<br>; interrupt priority<br>movep X:&lt;&lt;IPR,X:SavePR<br>movep #$3000,X:&lt;&lt;IPR</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>main:<br>; tady muzes posilat maxima (na pozadani)<br>;cekame na dva bloky dat<br>_rcvcom: jclr #0,X:&lt;&lt;HSR,_rcvcom<br>movep X:&lt;&lt;HRX,X0<br>jset #0,X0,ComAbort<br>jset #1,X0,ComGetPos<br>jset #2,X0,ComPutData<br>jset #3,X0,ComFadeOut<br>jset #4,X0,ComVolume<br>jset #5,X0,ComEffPar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComAbort:<br>movep #0,X:&lt;&lt;CRB ; disable interr.<br>movep X:SavePR,X:&lt;&lt;IPR ; vrat IPR<br>move Y:SavePR,SR<br>DummyLop:<br>_Wait: jclr #0,X:&lt;&lt;HSR,_Wait<br>movep X:&lt;&lt;HRX,X0 ; prevezmi vyp.<br>jmp DummyLop</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComGetPos:<br>; zjisti n&nbsp;skok џtecЎ hlavy pэed zapisovacЎ (voln‚ mЎsto)<br>move R2,A<br>move R1,X0<br>sub X0,A #&gt;DSPLenBuf,X0<br>jge &lt;_plus<br>add X0,A<br>_plus:<br>_sndpos: jclr #1,X:&lt;&lt;HSR,_sndpos<br>movep A,X:&lt;&lt;HTX<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComPutData:<br>; pэevezme data pro jeden blok zvuku<br>_rcvnkan: jclr #0,X:&lt;&lt;HSR,_rcvnkan<br>movep X:&lt;&lt;HRX,N7</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>move R1,R0\nmove M1,M0\n\nmove #eff_a_sum,R3\nmove #eff_b_sum,R4\nmove #FBRamPomer-1,M3\nmove M3,M4\n\nclr B\ndo #FBRamPomer,_ClrLop\n    move B,L:(R0)+\n    move B,L:(R3)+\n    move B,L:(R4)+\n_ClrLop:\n\ndo N7,_KanLop\n    _rcvbal: jclr #0,X:&lt;&lt;HSR,_rcvbal\n    movep X:&lt;&lt;HRX,Y0\n\n    move #0,N0\n\n    ; neprve fade (zhora)\n    tfr Y0,B #&gt;$ff0000,X1\n    tst B\n    jeq &lt;_Nezni\n\n    and X1,B1 #&gt;1&lt;&lt;(23-6),X1\n\n    ; je to 7b, ale o 6b posunuto nahoru\n    jeq &lt;_NoFade\n        move B1,X0\n        mpy X1,X0,B ; m&amp;nbsp;me rychlost fadeoutu rel. k celk. hlasitosti\n        bset #0,N0\n        move B1,X:FadeSpd\n    _NoFade:\n\n    tfr Y0,B #&gt;$ff,X0\n    and X0,B1 #&gt;1&lt;&lt;(23-8),X1\n    mpy X1,Y0,A B1,Y0\n    and X0,A1 #&gt;1&lt;&lt;(23-9),X1\n    ; Y0 a A1 obsahujЎ 8b balance\n    ; posuneme-li je o 9b dol…, dostaneme v A0,B0 23 balance\n    mpy Y0,X1,B A1,Y0\n    mpy Y0,X1,A\n\n    move A0,X1\n    move B0,X0\n    move X,L:KanVol\n\n    jclr #0,N0,_nocfade\n        move X:FadeSpd,Y0\n        ;move #0.01,Y0\n        mpy Y0,X1,A\n        mpy Y0,X0,B\n        move AB,L:FadeSpd\n    _nocfade:\n\n    _rcveff: jclr #0,X:&lt;&lt;HSR,_rcveff\n    movep X:&lt;&lt;HRX,Y0\n\n    move #0,N1 ; zaznamen&amp;nbsp;v&amp;nbsp;me pou§itЎ effekt…\n\n    tfr Y0,B #&gt;$ff,X0\n    and X0,B1 #&gt;1&lt;&lt;(23-8),X1\n    jeq &lt;_NoEffB\n        bset #1,N1\n        bset #1,X:EffBMap\n    _NoEffB:\n    mpy X1,Y0,A B1,Y0\n    and X0,A1 #&gt;1&lt;&lt;(23-9),X1\n    jeq &lt;_NoEffA\n        bset #0,N1\n        bset #0,X:EffBMap\n    _NoEffA:\n    ; Y0 a A1 obsahujЎ 8b balance\n    ; posuneme-li je o 9b dol…, dostaneme v A0,B0 23b sЎlu efektu\n    mpy Y0,X1,B A1,Y0\n    mpy Y0,X1,A\n\n    move A0,X:EffASila\n    move B0,X:EffBSila\n\n    move R1,R0\n    do #FBRamPomer,_AddLop\n        _rcvdata: jclr #0,X:&lt;&lt;HSR,_rcvdata\n        movep X:&lt;&lt;HRX,X0\n\n        ;move #&gt;(1&lt;&lt;12),X0\n\n        move L:KanVol,Y ; balance (frac)\n        mpy X0,Y1,A ; X0 je 16 bit… se znam‚nkem\n        mpy X0,Y0,B\n\n        move A,X1\n        move B,X0\n\n        ; v X m&amp;nbsp;me data pro zpracov&amp;nbsp;nЎ pэes efekty\n\n        ; pэiџЎt&amp;nbsp;me k hlavnЎmu bufferu\n        move L:(R0),AB\n        add X1,A\n        add X0,B\n        move AB,L:(R0)+\n\n        jclr #0,N1,_NoSumA\n            move X:EffASila,Y0\n            move L:(R3),AB\n            mac Y0,X1,A\n            mac Y0,X0,B\n            move AB,L:(R3)+\n        _NoSumA:\n        jclr #1,N1,_NoSumB\n            move X:EffBSila,Y0\n            move L:(R4),AB\n            mac Y0,X1,A\n            mac Y0,X0,B\n            move AB,L:(R4)+\n        _NoSumB:\n\n        jclr #0,N0,_NoIFade\n            move L:KanVol,AB\n            move L:FadeSpd,X\n            sub X1,A #0,Y1\n            tlt Y1,A\n            sub X0,B\n            tlt Y1,B\n            move AB,L:KanVol\n        _NoIFade:\n        nop\n\n    _AddLop:\n    _Nezni:\n    nop\n\n_KanLop:\n\n; spoџЎtej efekty\n\njclr #0,X:EffBMap,_NoATot\n    move #eff_a_sum,R4\n    move #eff_a_par,R5\n    move R1,R0\n    jsr &lt;EffEcho\n_NoATot:\n\njclr #1,X:EffBMap,_NoBTot\n    move #eff_b_sum,R4\n    move #eff_b_par,R5\n    move R1,R0\n    jsr &lt;EffEcho\n_NoBTot:\n\nmove R1,R0\ndo #FBRamPomer,_SatLop\n    move L:Volume,Y ; celkovм fade-out\n    clr B Y1,A\n    sub Y0,A L:(R0),X\n    tlt B,A\n    mpy X0,Y1,B A,X:Volume\n    mpy X1,Y1,A\n    rep #8\n    asl A\n    rep #8\n    asl B\n    move AB,L:(R0)+\n_SatLop:\n\nmove R0,R1 ; pэejdi na dalзЎ Јsek\n\njmp &lt;main</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>ComFadeOut:<br>_WaitL: jclr #0,X:&lt;&lt;HSR,_WaitL<br>movep X:&lt;&lt;HRX,Y:Volume<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComVolume:<br>_WaitL: jclr #0,X:&lt;&lt;HSR,_WaitL<br>movep X:&lt;&lt;HRX,X:Volume<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ComEffPar:<br>move #-1,M0<br>move #EffParTab,R0<br>_rcvnum: jclr #0,X:&lt;EffLenBuf-1,X1<br>mpy X0,X1,A<br>cmp X1,A<br>tgt X1,A<br>move A,X:(R0)<br>jmp &lt;main</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; efekty:<br>; v R4 dost&nbsp;v&nbsp; vstup<br>; v R5 dost&nbsp;v&nbsp; parametry<br>; do R0 m&nbsp; pэiџЎst vмstup</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; X:(R5) je delay-1 (tj. pэipraven pro M5)<br>; Y:(R5) je aktu&nbsp;lnЎ posice<br>; X:(R5)+1 je feedback<br>; X:(R5)+2 je gain</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>EffEcho:<br>; vezmi si parametry<br>move X:(R5),M3<br>move Y:(R5)+,R3<br>move X:(R5)+,Y0 ; feedback<br>move X:(R5)-,Y1 ; gain<br>move (R5)-</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>; proveФ efekt z R4 do R0\ndo #FBRamPomer,_echo_loop\n\n    ; vezmi si, co tebe zbylo z minula\n    move L:(R3),X\n\n    ; pэidej k tomu feedbackem novм vzorek\n    move L:(R4)+,AB\n    mac X1,Y0,A\n    mac X0,Y0,B\n    move AB,L:(R3)+\n\n    ; minulм vzorek pэidej podle gain do vмstupnЎho bufferu\n    move L:(R0),AB\n    mac X1,Y1,A\n    mac X0,Y1,B\n    move AB,L:(R0)+\n\n_echo_loop:\n\nmove R3,Y:(R5)\nrts</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>EndProg equ *<br>if @CVS(N,EndProg)&gt;$200<br>error \"Program not in internal RAM\"<br>endif</p>\n<!-- /wp:paragraph -->', 'COMPO.ASM', '', 'inherit', 'closed', 'closed', '', '86-revision-v1', '', '', '2023-05-13 13:44:09', '2023-05-13 13:44:09', '', 86, 'http://xboxcode.hstn.me/?p=87', 0, 'revision', '', 0),
