(96, 1, '2023-05-13 13:49:27', '2023-05-13 13:49:27', '<!-- wp:paragraph -->\n<p>; COMPO.ASM<br>; spojovani kanalu + stereofonie pro Componium<br>; SUMA 1/1994-2/1994</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>nolist\ninclude \"intequ.asm\"\ninclude \"ioequ.asm\"\nlist</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>; CRA bits------------------------------------------------<br>; 15 = PS = 0 : 0=/1, 1=/8, prescalar<br>; 14-13 = WL1-0 = 10 : 00=8, 01=12, 10=16, 11=24 bits (set to 16-bits now)<br>; 12-8 = DC4-DC0 = 0 0001 : divider control (normally word transfer rate)<br>; 7-0 = PM7-0 = $00 : divide by 1-256, 0=fastest (divide by 1)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRA_BITS EQU $4700<br>; ve skut. 16 kanalu (data+stereo)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; SSI CRB bits --------------------------------------------------<br>; CRB determines the serial modes<br>; 15 = RIE = 1 : 0=disable, 1=enable receive interrupt<br>; 14 = TIE = 1 : 0=disable, 1=enable transmit interrupt<br>; 13 = RE = 1 : 0=disable, 1=enable receive<br>; 12 = TE = 1 : 0=disable, 1=enable transmit</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 11 = MODE = 1 : 0=normal, 1=network<br>; 10 = GCK = 0 : 0=continuous, 1=gated<br>; 9 = SYN = 0 : 0=asynchronous, 1=synchronous, transmit and receive<br>; 8 = FSL0 = 0 : 0=word, 1=bit length, Frame Sync clock</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 7 = FSL1 = 0 : 0=TX and RX same length, 1=different<br>; 6 = SHFD = 0 : 0=MSB first, 1=LSB first<br>; 5 = SCKD = 0 : 0=external, 1=internal bit clock<br>; 4 = SCD2 = 0 : 0=input, 1=output on SC2, transmit frame sync</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; 3 = SCD1 = 0 : 0=input, 1=output on SC1, receive frame sync<br>; 2 = SCD0 = 0 : 0=input, 1=output on SC0, receive clock<br>; 1 = OF1 = 0 :<br>; 0 = OF0 = 0 : OF0 not to SC0 ; must match SCKD</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>CRB_BITS EQU $F800</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>intracks equ 8<br>ECHO equ 0<br>STEREO_TIME equ 0<br>INTERPOL equ 0</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org l:$0</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>in_data: ds intracks<em>2 ; strida se kanal/stereohlasitost vol_lr: ds intracks</em>2</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>out_1: dc 0<br>if INTERPOL<br>out_2: dc 0<br>ipol: dc 0<br>endif</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>if STEREO_TIME</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>buf_st_p: dc 0<br>endif</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>sync: dc 0<br>SavePR: ds 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>; zpozdeni je umerne hlasitosti<br>; pro min. hlasitos je zpozdeni max.<br>; zpozdeni = (1.0 - hlasitost) * len_st</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>if ECHO</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>len_eb equ 25000/20 ; delka ozveny - jakoby hall<br>echo_buf: dsm len_eb<br>endif<br>if STEREO_TIME<br>len_st equ 25000/2000 ; delka zpozdeni pro stereo - odpovida rozteci 60 cm<br>stereo_bufs: dsm len_st<br>sb_f: dsm len_st<br>dsm len_st<br>dsm len_st<br>dsm len_st<br>dsm len_st<br>dsm len_st<br>dsm len_st<br>off_st: equ sb_f-stereo_bufs<br>if intracks!=8<br>error \"# of tracks\"<br>endif<br>endif</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>org p:$0\njmp start\n\norg p:SSIRD\njsr datain\norg p:SSIRDE\njsr datain\n\norg p:SSITD\njsr dataout\norg p:SSITDE\njsr dataout\n\norg p:$40</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>datain:<br>jset #RFS,X:&lt;&lt;SSR,_din_sync<br>movep X:&lt;&lt;RX,X:(r7)+<br>rti<br>_din_sync:<br>movep X:&lt;&lt;RX,X:(r7)+<br>bset #0,X:&lt;sync<br>rti<br>if INTERPOL</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>dataout:<br>jset #0,X:&lt;ipol,_druhy_vz<br>jset #TFS,X:&lt;&lt;SSR,_dout_snc1<br>movep X:out_1,X:&lt;&lt;TX<br>rti<br>_dout_snc1:<br>bset #0,X:&lt;ipol<br>movep Y:out_1,X:&lt;&lt;TX<br>rti<br>_druhy_vz:<br>jset #TFS,X:&lt;&lt;SSR,_dout_snc2<br>movep X:out_2,X:&lt;&lt;TX<br>rti<br>_dout_snc2:<br>bclr #0,X:&lt;ipol<br>movep Y:out_2,X:&lt;&lt;TX<br>rti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>else</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>dataout:<br>jset #TFS,X:&lt;&lt;SSR,_dout_sync<br>movep X:out_1,X:&lt;&lt;TX<br>rti<br>_dout_sync:<br>movep Y:out_1,X:&lt;&lt;TX<br>rti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>endif</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>start:<br>;set port c to SSI + SCI<br>movep #$1F8,X:&lt;0,X:&lt;&lt;BCR<br>; interrupt priority<br>movep X:&lt;&lt;IPR,X:SavePR<br>movep #$3000,X:&lt;&lt;IPR<br>movep #1,X:PBC<br>; scaling mode na 0, interrupt na 0<br>andi #&lt;$F0,MR<br>main:<br>; tady muzes posilat maxima (na pozadani)<br>;cekame na dva bloky dat<br>jset #0,X:&lt;&lt;HSR,Abort<br>_rcvstereo: jclr #0,X:&lt;sync,_rcvstereo<br>bclr #0,X:&lt;sync<br>_rcvdata: jclr #0,X:&lt;sync,_rcvdata<br>bclr #0,X:&lt;sync<br>; rozdel stereo na dve casti<br>do #intracks,_rlloop<br>clr A #$ff0000,Y0<br>move X:(R4)+,A1 ; nacti stereo<br>tfr A,B (R4)+ ; preskoc data<br>and Y0,A1<br>asr A #$00ff00,Y0<br>and Y0,B1 A,X:(R3)+<br>rep #7<br>asl B<br>move B,X:(R3)+<br>_rlloop:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>if !STEREO_TIME\n    clr A #0,B\n    do #intracks,AddLoop\n        move (R4)+\n        move X:(R4)+,X0 ; nacti data\n        move X:(R3)+,X1 ; X1 = hlasitost\n        mac X0,X1,A X:(R3)+,X1\n        mac X0,X1,B\n    AddLoop:\nelse\n;;  move M5,Y0 ; Y0 = (len_st-1)&gt;&gt;23,\n    move #0,Y0\n    move #&gt;off_st,Y1\n    move R5,X:&lt;buf_st_p\n    do #intracks,InLoop\n;       move #$ffff,M5\n;       move R5,B\n;       add Y1,B\n;       move B,R5\n;;  move #&gt;off_st,N5\n;;  nop\n;;  move (R5)+N5\n;       move #len_st-1,M5\n\n        move (R4)+ ; preskoc stereo\n        move X:(R4)+,X0 ; nacti data\n;       move X0,X:(R5) ; uloz aktual. vzorek do prisl. kanalu\n\n        move X:(R3),X1 ; X1 = hlasitost\n\n;       move #&gt;1,B\n;       mac Y0,X1,B ; B=(len_st-1*hlasitost)&gt;&gt;23+1 - vypocti zpozdeni\n;       move B,N5\n;       nop\n;       move (R5)+N5\n;       move X:(R5),X0 ; bere se kruhem - skut. zpozdeni je len_st-B\n;       move (R5)-N5\n        mpy X0,X1,B\n        move B,Y:(R3)+ ; uloz vysledek L-kanalu\n\n        move X:(R3),X1 ; X1 = hlasitost\n;       move #&gt;1,B\n;       mac Y0,X1,B ; B je zpozdeni\n;       move B,N5\n;       nop\n;       move (R5)+N5\n;       move X:(R5),X0 ; bere se kruhem - skut. zpozdeni je len_st-B\n;       move (R5)-N5\n        mpy X0,X1,B\n        move B,Y:(R3)+ ; uloz vysledek P-kanalu\n\n    InLoop:\n\n    move X:&lt;buf_st_p,R5 ; vrat R5\n    nop\n    move (R5)+\n\n    clr A #0,B\n    do #intracks,AddLoop\n        move Y:(R3)+,X0\n        add X0,A Y:(R3)+,X0\n        add X0,B\n    AddLoop:\n\nendif\n\nasr a ; pro zmenseni zkresleni radeji snizime hlasitost\nasr b\nif ECHO\n    move AB,L:(R6)+ ; automaticky limituje\n    move L:(R6),X ; nejvzdalenejsi\n    move #len_eb/2,N6 ; priprav adresu dalsiho vzorku\n    move #0.10,Y0\n    mac X1,Y0,A\n    mac X0,Y0,B\n    move L:(R6+N6),X ; v polovine\n    move #len_eb/4,N6 ; priprav adresu dalsiho vzorku\n    move #0.35,Y0\n    mac X1,Y0,A\n    mac X0,Y0,B\n    move L:(R6+N6),X ; ve ctvrtine\n    move #0.45,Y0\n    mac X1,Y0,A\n    mac X0,Y0,B\nendif\nif INTERPOL\n    move L:&lt;out_2,X\n    ; priprav interpolaci 25 kHz na 50 kHz\n    move AB,L:&lt;out_2\n    add X1,A\n    asr A ; A = (A+X1)/2 - levy kanal\n    add X0,B\n    asr B ; B = (B+X0)/2 - pravy kanal\n    move AB,L:&lt;out_1\n    bclr #0,X:&lt;ipol ; urci poradi vysilani vzorku\nelse\n    move AB,L:&lt;out_1\nendif\njmp   &lt;main</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Abort:<br>movep X:&lt;$200<br>error \"Program not in internal RAM\"<br>endif</p>\n<!-- /wp:paragraph -->', 'COMPO0.ASM', '', 'publish', 'open', 'open', '', 'compo0-asm', '', '', '2023-05-13 13:49:29', '2023-05-13 13:49:29', '', 0, 'http://xboxcode.hstn.me/?p=96', 0, 'post', '', 0),
