t;0 -&gt; vstup z MIDI, Asc je klavesa, Scan je velocity */<br>int LHan=VrchniSekv();<br>int ret=0;<br>if( LHan&gt;=0 )<br>{<br>OknoInfo *OI=OInfa[LHan];<br>SekvKursor k;<br>Err err;<br>RZapisK(&amp;k,AktK(&amp;OI-&gt;Kurs));<br>err=TestK(&amp;k);<br>if( ( err==KON || BufK(&amp;k)[0]&gt;PPau ) &amp;&amp; JeTon(Asc,Shf) )<br>{<br>err=VlozTon(OI,False);<br>if( err==OK ) err=TestK(&amp;k);<br>}<br>if( err==OK )<br>{<br>int Druh=BufK(&amp;k)[0];<br>if( Druh&lt;=PPau ) /* editace noty <em>/ { int Ton,Oktava; int Delka=BufK(&amp;k)[2],Attr=BufK(&amp;k)[1]; Flag BTec=BezTecky(Delka),BTri=BezTrioly(Delka); if( !BTec ) Delka=PrepTecka(Delka); if( !BTri ) Delka=PrepTriola(Delka); if( Druh=0 ) switch( Asc ) { case \'c\': case \'C\': Ton=TC;goto TonLab; case \'d\': case \'D\': Ton=TD;goto TonLab; case \'e\': case \'E\': Ton=TE;goto TonLab; case \'f\': case \'F\': Ton=TF;goto TonLab; case \'g\': case \'G\': Ton=TG;goto TonLab; case \'a\': case \'A\': Ton=TA;goto TonLab; case \'h\': case \'H\': Ton=TH;goto TonLab; case \'b\': case \'B\': Ton=TH;goto TonLab; case \'p\': case \'P\': Ton=PPau;Attr&amp;=~(KBOMaskA|Leg);Oktava=0;goto OktLab; case \'1\': Delka=Takt; goto OktLab; case \'2\': Delka=Takt/2; goto OktLab; case \'4\': Delka=Takt/4; goto OktLab; case \'8\': Delka=Takt/8; goto OktLab; case \'9\': Delka=Takt/16; goto OktLab; case \'0\': Delka=Takt/32; goto OktLab; case \'.\': BTec^=True; goto OktLab; case \'3\': BTri^=True; goto OktLab; case \'/\': if( Ton!=PPau ) Attr^=Leg; goto OktLab; case \'+\': if( Ton!=PPau &amp;&amp; Oktava0 ) Oktava--; goto OktLab; case \'(\': if( Ton!=PPau ) if( (Attr&amp;KBOMaskA)==KrizekA ) Attr&amp;=~KrizekA; else Attr&amp;=~KBOMaskA,Attr|=KrizekA; goto OktLab; case \')\': if( Ton!=PPau ) if( (Attr&amp;KBOMaskA)==BeckoA ) Attr&amp;=~BeckoA; else Attr&amp;=~KBOMaskA,Attr|=BeckoA; goto OktLab; case \'</em>\': if( Ton!=PPau ) if( (Attr&amp;KBOMaskA)==OdrazA ) Attr&amp;=~OdrazA; else Attr&amp;=~KBOMaskA,Attr|=OdrazA; goto OktLab;<br>TonLab:<br>if( Shf&amp;K_LSHIFT ) Oktava=NormOkt;<br>else if( Shf&amp;K_RSHIFT ) Oktava=NormOkt-2;<br>else Oktava=NormOkt-1;<br>OktLab:<br>if( !BTri ) Delka=PrepTriola(Delka);<br>if( !BTec &amp;&amp; Ton!=PPau ) Delka=PrepTecka(Delka);<br>BufK(&amp;k)[0]=Ton+Okt<em>Oktava; BufK(&amp;k)[1]=Attr; BufK(&amp;k)[2]=Delka; BufK(&amp;k)[3]=0; PlatiProc(PisK(&amp;k),==OK); if( Attr&amp;Soubezna ) KonTon(&amp;k); TNormujK(&amp;k,AktK(&amp;OI-&gt;Kurs)); /</em>KresliLab:<em>/ UkazKursor(OI); Kresli(VrchniSekv()); break; default: ret=-1; } else /</em> MIDI <em>/ { int T,A; if( MidiKonvTon(&amp;k,&amp;T,&amp;A,Asc) ) { if( T&gt;NOkt</em>Okt ) T=PPau;<br>else if( T&lt;0 ) T=PPau;<br>BufK(&amp;k)[0]=T;<br>BufK(&amp;k)[1]=(Attr&amp;~KBOMaskA)|A;<br>BufK(&amp;k)[2]=Delka;<br>BufK(&amp;k)[3]=0;<br>PlatiProc(PisK(&amp;k),==OK);<br>UkazKursor(OI);<br>Kresli(VrchniSekv());<br>}<br>}<br>}<br>}<br>else ret=-1;<br>ZavriK(&amp;k);<br>}<br>else ret=-1;<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static int NajdiEdit( SekvKursor *s, SekvSoubor *z, enum CPovel Povel, CasT CasPos )<br>{<br>/* 0 - nenalezen povel, nalezena pozice <em>/ /</em> &lt;0 - neexeist. pozice */ /* &gt;0 - nalezen povel na pozici */<br>int ret;<br>SekvSoubor r;<br>RozdvojS(&amp;r,z);<br>if( Povel!=PRep &amp;&amp; Povel!=PERep )<br>{<br>NajdiSCasS(&amp;r,CasPos);<br>for(;;)<br>{<br>if( ZpatkyS(&amp;r)!=OK ) break;<br>if( CasJeS(&amp;r)!=CasPos ) {JCtiS(&amp;r);break;}<br>if( TestS(&amp;r)!=OK || BufS(&amp;r)[0]==Povel ) break;<br>}<br>}<br>if( CasJeS(&amp;r)!=CasPos ) ret=-1;<br>else if( TestS(&amp;r)==EOK &amp;&amp; BufS(&amp;r)[0]==Povel ) ret=+1;<br>else ret=0;<br>RZapisK(s,AktK(&amp;r));<br>ZavriS(&amp;r);<br>TestK(s);<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int NajdiPovel( SekvKursor *s, SekvSoubor *z, enum CPovel Povel )<br>{ /* viz NajdiEdit */<br>SekvSoubor r;<br>RozdvojS(&amp;r,z);<br>NajdiSCasS(&amp;r,CasJeS(&amp;r));<br>for(;;)<br>{<br>if( ZpatkyS(&amp;r)!=OK ) break;<br>PlatiProc( TestS(&amp;r), ==EOK );<br>if( BufS(&amp;r)[0]==Povel ) break;<br>}<br>RZapisK(s,AktK(&amp;r));<br>ZavriS(&amp;r);<br>return TestK(s)==EOK &amp;&amp; s-&gt;Buf[0]==Povel;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">if 0</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>int InitEdit( SekvKursor *s, JednItem Povel, int Kan, CasT CasPos, Flag Hledat )<br>{<br>OknoInfo *OI=OInfa[VrchniSekv()];<br>SekvSoubor <em>z; int ret=-1; /</em> Kan&lt;0 znamena ediatci na pozici kursoru */ if( Kan&lt;0 || Kan==OI-&gt;KKan &amp;&amp; ( Povel==PRep || Povel==PERep) )<br>{<br>RZapisK(s,AktK(&amp;OI-&gt;Kurs));<br>if( TestK(s)!=EOK ) return 0;<br>return BufK(s)[0]==Povel;<br>}<br>z=&amp;OI-&gt;Soub[Kan];<br>if( Povel&gt;=0 )<br>{<br>SekvSoubor zk;<br>RozdvojS(&amp;zk,z);<br>NajdiSCasS(&amp;zk,CasPos);<br>if( !Hledat ) ret=NajdiEdit(s,&amp;zk,Povel,CasPos);<br>else ret=NajdiPovel(s,&amp;zk,Povel);<br>ZavriS(&amp;zk);<br>}<br>else if( Povel==KON )<br>{<br>RZapisK(s,AktK(z));<br>NajdiSCasK(s,CasPos);<br>}<br>else if( Povel==ZAC )<br>{<br>RZapisK(s,AktK(z));<br>NajdiCasK(s,CasPos);<br>}<br>else Plati( False );<br>return ret;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">endif</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>static int SNajdiEdit( SekvSoubor *s, SekvSoubor *z, enum CPovel Povel, CasT CasPos )<br>{<br>/* 0 - nenalezen povel, nalezena pozice <em>/ /</em> &lt;0 - neexeist. pozice */ /* &gt;0 - nalezen povel na pozici */<br>int ret;<br>SekvSoubor r;<br>RozdvojS(&amp;r,z);<br>if( Povel!=PRep &amp;&amp; Povel!=PERep )<br>{<br>NajdiSCasS(&amp;r,CasPos);<br>for(;;)<br>{<br>if( ZpatkyS(&amp;r)!=OK ) break;<br>if( CasJeS(&amp;r)!=CasPos ) {JCtiS(&amp;r);break;}<br>if( TestS(&amp;r)!=OK || BufS(&amp;r)[0]==Povel ) break;<br>}<br>}<