(112, 1, '2023-05-13 13:54:48', '2023-05-13 13:54:48', '<!-- wp:paragraph -->\n<p>/* digitalni hrani na Falconu <em>/ /</em> SUMA 3/1993-3/1994 <em>/ /</em> nejnizsi uroven - pro obecne pouziti */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"faldigia.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"llhraj.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"digill.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">include \"3D\\GRAVON\\sndGiGOut.h\"</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>/* FadeOuty: 18. 8., suggested by COMPO */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define _TIMERD 1 /* zda bezi pomaly player v preruseni */</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define _TDA 1 /* zda je TimerD ve skutecnosti A */</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define TimerCDC ( *(char *)0xfffffa1dL )</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define TimerAC ( *(char *)0xfffffa19L )</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">define FBRamPomer 128</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>double FBufFreqDbl;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static const short FTichoObs[]={0};<br>static const NastrSoub FTichoSoub={\"\",FTichoObs,1};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>FNastroj FalcKans[MaxFalcKan];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* zakladni implementace hrani <em>/ /</em> hlavni hraci procedury <em>/ /</em> implementace pro F030 */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void F_FSetEffect( int A, int B, int Kan )<br>{ /* A a B jsou od 0 do 255 */<br>FNastroj *N=&amp;FalcKans[Kan];<br>N-&gt;EffA=A;<br>N-&gt;EffB=B;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void F_FSetNastr( int NI, int Kan, DynPole *P )<br>{<br>if( NI&gt;=0 )<br>{<br>const NastrDef *NN=NastrAcc(P,NI);<br>FalcKans[Kan].NasDef=NN;<br>}<br>else<br>{<br>FalcKans[Kan].NasDef=NULL;<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void F_FEchoNastr( int NI, int Kan, DynPole <em>P ) { /</em> automatick‚ efekty */<br>FNastroj *N=&amp;FalcKans[Kan];<br>const NastrSoub *NS;<br>if( NI&gt;=0 )<br>{<br>const NastrDef *NN=NastrAcc(P,NI);<br>N-&gt;NasDef=NN;<br>NS=AccDyn(&amp;NSou,NN-&gt;IMSoubory[0]);<br>}<br>else<br>{<br>N-&gt;NasDef=NULL;<br>NS=&amp;FTichoSoub;<br>}<br>N-&gt;EffA=NS-&gt;EffA;<br>N-&gt;EffB=NS-&gt;EffB;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>void F_FSetStereo( int VMin, int VMax, int Kan )<br>{<br>FNastroj *K=&amp;FalcKans[Kan];<br>K-&gt;StereoMin=VMin,K-&gt;StereoMax=VMax;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int FadeSpd=80;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int F_FyzTon( int Ton )<br>{<br>Ton-=CisMinOkt<em>12; while( Ton&lt;0 ) Ton+=12; while( Ton&gt;=FyzOkt</em>12 ) Ton-=12;<br>return Ton;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void FFSetNastr( int Kan, int Vol, int Ton, Flag Legato )<br>{ /* verze pro Falcona */<br>FNastroj *N=&amp;FalcKans[Kan];<br>if( Vol&gt;0 ) /* Note On <em>/ { /</em> stereo tabulky a hlasitost <em>/ /</em> Ton je 7 bit unsigned , max-min je 8 bit signed (max 0x101) */<br>int v;<br>long Rep,Krk,RepF;<br>const NastrDef *NS=N-&gt;NasDef;<br>const NastrSoub <em>NI; #define KrokLog 24 Ton=F_FyzTon(Ton); if( !NS ) NI=&amp;FTichoSoub; else NI=AccDyn(&amp;NSou,NS-&gt;IMSoubory[Ton]); Krk=NI-&gt;TonKrok[Ton]; /</em> 32(.KrokLog) bit… <em>/ /</em> musime poslat hlasitosti <em>/ /</em> Ton je 7 bit unsigned , max-min je 8 bit unsigned (max 0xfe) <em>/ v=Ton</em>(N-&gt;StereoMax-N-&gt;StereoMin)/(12<em>FyzOkt)+N-&gt;StereoMin; /</em> v je 8 bitu signed <em>/ /</em> 8b * 7b je 15 b unsigned, musi vyjit 8b unsigned <em>/ N-&gt;LVol=((0x80-v)</em>Vol)&gt;&gt;7;<br>N-&gt;PVol=((0x80+v)<em>Vol)&gt;&gt;7; N-&gt;Fade=0; Rep=NI-&gt;Repet; /</em> Rep musi byt 32.0 <em>/ RepF=0; if( (Krk&gt;&gt;KrokLog)&gt;(NI-&gt;Delka-NI-&gt;Vynech) ) Krk=0; if( Rep&gt;NI-&gt;Delka-NI-&gt;Vynech ) Rep=0; if( Krk==0 || Kan==0 &amp;&amp; Krk&gt;0 &amp;&amp; Rep&lt;=(Krk&gt;&gt;KrokLog) ) /</em> v ost. kanalech smi byt Rep==0 */<br>{<br>Rep=Krk&gt;&gt;KrokLog;<br>RepF=(Krk&amp;((1L&lt;RNas = (short *)NI-&gt;Obs+(NI-&gt;Delka-NI-&gt;Vynech); /* konec nastroje <em>/ N-&gt;RPos = -(NI-&gt;Delka-NI-&gt;Vynech); /</em> offset vzdy zaporny <em>/ N-&gt;RPosF = 0; } N-&gt;RKrk = Krk&gt;&gt;KrokLog; N-&gt;RKrkF = (Krk&amp;((1L&lt;RRep=Rep; N-&gt;RRepF=RepF; } ef( Vol==0 ) { /</em> Note Off <em>/ N-&gt;RNas=FTichoObs+lenof(FTichoObs); /</em> konec nastroje <em>/ N-&gt;RPos=-(int)lenof(FTichoObs); /</em> offset vzdy zaporny <em>/ N-&gt;RKrk=0; N-&gt;RRep=0; N-&gt;RPosF=0; N-&gt;RKrkF=0; N-&gt;RRepF=0; N-&gt;Fade=0; } else /</em> FadeOut */<br>{<br>N-&gt;Fade=FadeSpd;<br>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag F_FHrajTon( int Ton, int Vol, int Kan )<br>{<br>FFSetNastr(Kan&amp;0x3f,Vol,Ton,Kan&amp;0x40);<br>return False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag Pauza;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static CasT DelkaSkl;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* oprava podle reklamace COMPO - 11.8. */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag F_Display=True;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag F_Mode2 = False;<br>int F_Mode=8; /* poџet kan&nbsp;l… <em>/ int F_Divis=CLK25K; /</em> эЎzenЎ frekvence */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int F_Povel; /* r…zn‚ povely */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const int sampleSizeTgt = sizeof(short)*2;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static __inline short Sat16b( int x )<br>{<br>if (xSHRT_MAX) return SHRT_MAX;<br>return x;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const int volMusic = 2;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum {EchoBufSize=4*1024};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>struct EchoBuffer<br>{<br>int buffer[2][EchoBufSize];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int accum[2]; // accumulate left/right input in this sample<br>int bufPos;<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static struct EchoBuffer EchoA,EchoB;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int EffPars[2][4]=<br>{<br>{1000,700,1000,0},<br>{ 330,400,1000,0},<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void EchoAddInput(struct EchoBuffer *echo, int lData, int rData)<br>{<br>echo-&gt;accum[0] += lData, echo-&gt;accum[1]+= rData;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void EchoSample(struct EchoBuffer *echo, int *effPars, int *lOut, int *rOut)<br>{<br>// perform echo simulation<br>int lIn = echo-&gt;accum[0];<br>int rIn = echo-&gt;accum[1];<br>int delay = effPars[0]; // in samples<br>int feedback = effPars[1]; // in 1/1000<br>int gain = effPars[2]; // in 1/1000<br>int bufPos = echo-&gt;bufPos;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int bufL = echo-&gt;buffer[0][bufPos];<br>int bufR = echo-&gt;buffer[1][bufPos];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>delay = (int)(delay*FBufFreqDbl/32780);</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>echo-&gt;buffer[0][(bufPos+delay)%EchoBufSize] = lIn+bufL<em>feedback/1000; echo-&gt;buffer[1][(bufPos+delay)%EchoBufSize] = rIn+bufR</em>feedback/1000;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><em>lOut += bufL</em>gain/1000;<br><em>rOut += bufR</em>gain/1000;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// reset input data (prepare next sample)<br>echo-&gt;accum[0] = echo-&gt;accum[1] = 0;<br>echo-&gt;bufPos= (bufPos+1)%EchoBufSize;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag Echos = True;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void MixChannels(void *buffer, size_t bufferSize)<br>{<br>short *buffer16 = (short *)buffer;<br>size_t s;<br>size_t bufferSamples = bufferSize/sampleSizeTgt;<br>for (s=0; sRNas[nastr-&gt;RPos];<br>long long pos64 = (((long long)nastr-&gt;RPos)&lt;&lt;32)|nastr-&gt;RPosF;<br>long long krk64 = (((long long)nastr-&gt;RKrk)&lt;&lt;32)|nastr-&gt;RKrkF;<br>pos64 += krk64;<br>if (pos64&gt;=0) // positive means over end, loop or terminate<br>{<br>long long rep64 = (((long long)nastr-&gt;RRep)&lt;&lt;32)|nastr-&gt;RRepF;<br>pos64 -= rep64;<br>if (pos64&gt;=0) // if there is no repeat, return to the last sample<br>{<br>pos64 -= krk64;<br>}<br>}<br>nastr-&gt;RPos = pos64&gt;&gt;32, nastr-&gt;RPosF = (unsigned)pos64;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>  {\n    int lVol = sample*nastr-&gt;LVol/255;\n    int rVol = sample*nastr-&gt;PVol/255;\n    left += lVol;\n    right += rVol;\n\n    EchoAddInput(&amp;EchoA,lVol*nastr-&gt;EffA/255,rVol*nastr-&gt;EffA/255);\n    EchoAddInput(&amp;EchoB,lVol*nastr-&gt;EffB/255,rVol*nastr-&gt;EffB/255);\n  }\n}\nEchoSample(&amp;EchoA,EffPars&#91;0],&amp;left,&amp;right);\nEchoSample(&amp;EchoB,EffPars&#91;1],&amp;left,&amp;right);\n\nbuffer16&#91;s*2+0] = Sat16b(left&gt;&gt;volMusic);\nbuffer16&#91;s*2+1] = Sat16b(right&gt;&gt;volMusic);</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>}<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum doplnmode {DNormal,DZac,DKonec,DPauza};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static volatile Flag TimerKonec;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static size_t CompoMixerCallback(void <em>buffer, size_t bufferSize, int bufferFreq) { enum doplnmode Mode = DNormal; // each buffer process high-level commands (song) int bufferOffset = 0; // we want 200 Hz freq. or better for high-level size_t chunk = bufferFreq/200/sampleSizeTgt</em>sampleSizeTgt;<br>if (chunk&gt;bufferSize) chunk = bufferSize;<br>if (bufferSize==0) return 0;<br>while (bufferOffset+chunk&lt;=bufferSize)<br>{<br>if( Mode==DNormal )<br>{<br>// measure time based on samples processed<br>TimerADF(chunk/sampleSizeTgt<em>sec/bufferFreq); /</em> Zac i Kon - ticho */<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>// loop through all channels\nMixChannels((char *)buffer+bufferOffset,chunk);\nbufferOffset += chunk;</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>// now mix the channels<br>return bufferOffset;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">if _TIMERD</h1>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>static int FGetVol( int GVol&#91;MaxFyzKan] )\n{\n    (void)GVol;\n    return 8;\n}\n\nstatic CasT FGetCas( void ) /* zpozdeni */\n{\n    return 0;\n}<