FFSetNastr(Kan&amp;0x3f,Vol,Ton,Kan&amp;0x40);<br>return False;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static Flag Pauza;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static CasT DelkaSkl;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>/* oprava podle reklamace COMPO - 11.8. */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag F_Display=True;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flag F_Mode2 = False;<br>int F_Mode=8; /* poџet kan&nbsp;l… <em>/ int F_Divis=CLK25K; /</em> эЎzenЎ frekvence */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int F_Povel; /* r…zn‚ povely */</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const int sampleSizeTgt = sizeof(short)*2;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static __inline short Sat16b( int x )<br>{<br>if (xSHRT_MAX) return SHRT_MAX;<br>return x;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>const int volMusic = 2;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>enum {EchoBufSize=4*1024};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>struct EchoBuffer<br>{<br>int buffer[2][EchoBufSize];</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>int accum[2]; // accumulate left/right input in this sample<br>int bufPos;<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static struct EchoBuffer EchoA,EchoB;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static int EffPars[2][4]=<br>{<br>{1000,700,1000,0},<br>{ 330,400,1000,0},<br>};</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void EchoAddInput(struct EchoBuffer *echo, int lData, int rData)<br>{<br>echo-&gt;accum[0] += lData, echo-&gt;accum[1]+= rData;<br>}</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>static void EchoSample(struct EchoBuffer *echo, int *effPars, int *lOut, int *rOut)<br>{<br>// perform echo simulation<br>int lIn = echo-&gt;accum[0];<br>int rIn = echo-&gt;accum[1];<br