(141, 1, '2023-05-29 17:36:40', '2023-05-29 17:36:40', '<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">/// Removes a specific item from a list.\n/// @author Hugo Giraudel\n/// @param {list} $list List.\n/// @param {integer} $index Index.\n/// @return {list} Updated list.\n@function remove-nth($list, $index) {\n\n	$result: null;\n\n	@if type-of($index) != number {\n		@warn \"$index: #{quote($index)} is not a number for `remove-nth`.\";\n	}\n	@else if $index == 0 {\n		@warn \"List index 0 must be a non-zero integer for `remove-nth`.\";\n	}\n	@else if abs($index) &gt; length($list) {\n		@warn \"List index is #{$index} but list is only #{length($list)} item long for `remove-nth`.\";\n	}\n	@else {\n\n		$result: ();\n		$index: if($index &lt; 0, length($list) + $index + 1, $index);\n\n		@for $i from 1 through length($list) {\n\n			@if $i != $index {\n				$result: append($result, nth($list, $i));\n			}\n\n		}\n\n	}\n\n	@return $result;\n\n}\n\n/// Gets a value from a map.\n/// @author Hugo Giraudel\n/// @param {map} $map Map.\n/// @param {string} $keys Key(s).\n/// @return {string} Value.\n@function val($map, $keys...) {\n\n	@if nth($keys, 1) == null {\n		$keys: remove-nth($keys, 1);\n	}\n\n	@each $key in $keys {\n		$map: map-get($map, $key);\n	}\n\n	@return $map;\n\n}\n\n/// Gets a duration value.\n/// @param {string} $keys Key(s).\n/// @return {string} Value.\n@function _duration($keys...) {\n	@return val($duration, $keys...);\n}\n\n/// Gets a font value.\n/// @param {string} $keys Key(s).\n/// @return {string} Value.\n@function _font($keys...) {\n	@return val($font, $keys...);\n}\n\n/// Gets a misc value.\n/// @param {string} $keys Key(s).\n/// @return {string} Value.\n@function _misc($keys...) {\n	@return val($misc, $keys...);\n}\n\n/// Gets a palette value.\n/// @param {string} $keys Key(s).\n/// @return {string} Value.\n@function _palette($keys...) {\n	@return val($palette, $keys...);\n}\n\n/// Gets a size value.\n/// @param {string} $keys Key(s).\n///