(143, 1, '2023-05-29 17:37:05', '2023-05-29 17:37:05', '<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">// html-grid.scss v1.0 | @ajlkn | MIT licensed */\n\n// Mixins.\n\n	/// Initializes the current element as an HTML grid.\n	/// @param {mixed} $gutters Gutters (either a single number to set both column/row gutters, or a list to set them individually).\n	/// @param {mixed} $suffix Column class suffix (optional; either a single suffix or a list).\n	@mixin html-grid($gutters: 1.5em, $suffix: \'\') {\n\n		// Initialize.\n			$cols: 12;\n			$multipliers: 0, 0.25, 0.5, 1, 1.50, 2.00;\n			$unit: 100% / $cols;\n\n			// Suffixes.\n				$suffixes: null;\n\n				@if (type-of($suffix) == \'list\') {\n					$suffixes: $suffix;\n				}\n				@else {\n					$suffixes: ($suffix);\n				}\n\n			// Gutters.\n				$guttersCols: null;\n				$guttersRows: null;\n\n				@if (type-of($gutters) == \'list\') {\n\n					$guttersCols: nth($gutters, 1);\n					$guttersRows: nth($gutters, 2);\n\n				}\n				@else {\n\n					$guttersCols: $gutters;\n					$guttersRows: 0;\n\n				}\n\n		// Row.\n			display: flex;\n			flex-wrap: wrap;\n			box-sizing: border-box;\n			align-items: stretch;\n\n			// Columns.\n				&gt; * {\n					box-sizing: border-box;\n				}\n\n			// Gutters.\n				&amp;.gtr-uniform {\n					&gt; * {\n						&gt; :last-child {\n							margin-bottom: 0;\n						}\n					}\n				}\n\n			// Alignment.\n				&amp;.aln-left {\n					justify-content: flex-start;\n				}\n\n				&amp;.aln-center {\n					justify-content: center;\n				}\n\n				&amp;.aln-right {\n					justify-content: flex-end;\n				}\n\n				&amp;.aln-top {\n					align-items: flex-start;\n				}\n\n				&amp;.aln-middle {\n					align-items: center;\n				}\n\n				&amp;.aln-bottom {\n					align-items: flex-end;\n				}\n\n		// Step through suffixes.\n			@each $suffix in $suffixes {\n\n				// Suffix.\n					@if ($suffix != \'\') {\n						$suffix: \'-\' + $suffix;\n					}\n					@else {\n						$suffix: \'\';\n					}\n\n				// Row.\n\n					// Important.\n						&gt; .imp#{$suffix} {\n							order: -1;\n						}\n\n					// Columns, offsets.\n						@for $i from 1 through $cols {\n							&gt; .col-#{$i}#{$suffix} {\n								width: $unit * $i;\n							}\n\n							&gt; .off-#{$i}#{$suffix} {\n								margin-left: $unit * $i;\n							}\n						}\n\n					// Step through multipliers.\n						@each $multiplier in $multipliers {\n\n							// Gutters.\n								$class: null;\n\n								@if ($multiplier != 1) {\n									$class: \'.gtr-\' + ($multiplier * 100);\n								}\n\n								&amp;#{$class} {\n									margin-top: ($guttersRows * $multiplier * -1);\n									margin-left: ($guttersCols * $multiplier * -1);\n\n									&gt; * {\n										padding: ($guttersRows * $multiplier) 0 0 ($guttersCols * $multiplier);\n									}\n\n									// Uniform.\n										&amp;.gtr-uniform {\n											margin-top: $guttersCols * $multiplier * -1;\n\n											&gt; * {\n												padding-top: $guttersCols * $multiplier;\n											}\n										}\n\n								}\n\n						}\n\n			}\n\n	}</pre>\n<!-- /wp:preformatted -->', '_html-grid.scss', '', 'publish', 'open', 'open', '', '_html-grid-scss', '', '', '2023-05-29 17:37:06', '2023-05-29 17:37:06', '', 0, 'http://step-by-step-ldx1ik2x.hstn.me/?p=143', 0, 'post', '', 0),
