(145, 1, '2023-05-29 17:37:35', '2023-05-29 17:37:35', '<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">/// Makes an element\'s :before pseudoelement a FontAwesome icon.\n/// @param {string} $content Optional content value to use.\n/// @param {string} $category Optional category to use.\n/// @param {string} $where Optional pseudoelement to target (before or after).\n@mixin icon($content: false, $category: regular, $where: before) {\n\n	text-decoration: none;\n\n	&amp;:#{$where} {\n\n		@if $content {\n			content: $content;\n		}\n\n		-moz-osx-font-smoothing: grayscale;\n		-webkit-font-smoothing: antialiased;\n		display: inline-block;\n		font-style: normal;\n		font-variant: normal;\n		text-rendering: auto;\n		line-height: 1;\n		text-transform: none !important;\n\n		@if ($category == brands) {\n			font-family: \'Font Awesome 5 Brands\';\n		}\n		@elseif ($category == solid) {\n			font-family: \'Font Awesome 5 Free\';\n			font-weight: 900;\n		}\n		@else {\n			font-family: \'Font Awesome 5 Free\';\n			font-weight: 400;\n		}\n\n	}\n\n}\n\n/// Applies padding to an element, taking the current element-margin value into account.\n/// @param {mixed} $tb Top/bottom padding.\n/// @param {mixed} $lr Left/right padding.\n/// @param {list} $pad Optional extra padding (in the following order top, right, bottom, left)\n/// @param {bool} $important If true, adds !important.\n@mixin padding($tb, $lr, $pad: (0,0,0,0), $important: null) {\n\n	@if $important {\n		$important: \'!important\';\n	}\n\n	$x: 0.1em;\n\n	@if unit(_size(element-margin)) == \'rem\' {\n		$x: 0.1rem;\n	}\n\n	padding: ($tb + nth($pad,1)) ($lr + nth($pad,2)) max($x, $tb - _size(element-margin) + nth($pad,3)) ($lr + nth($pad,4)) #{$important};\n\n}\n\n/// Encodes a SVG data URL so IE doesn\'t choke (via codepen.io/jakob-e/pen/YXXBrp).\n/// @param {string} $svg SVG data URL.\n/// @return {string} Encoded SVG data URL.\n@function svg-url($svg) {\n\n	$svg: str-replace($svg, \'\"\', \'\\\'\');\n	$svg: str-replace($svg, \'%\', \'%25\');\n	$svg: str-replace($svg, \'&lt;\', \'%3C\');\n	$svg: str-replace($svg, \'&gt;\', \'%3E\');\n	$svg: str-replace($svg, \'&amp;\', \'%26\');\n	$svg: str-replace($svg, \'#\', \'%23\');\n	$svg: str-replace($svg, \'{\', \'%7B\');\n	$svg: str-replace($svg, \'}\', \'%7D\');\n	$svg: str-replace($svg, \';\', \'%3B\');\n\n	@return url(\"data:image/svg+xml;charset=utf8,#{$svg}\");\n\n}</pre>\n<!-- /wp:preformatted -->', '_mixins.scss', '', 'publish', 'open', 'open', '', '_mixins-scss', '', '', '2023-05-29 17:37:37', '2023-05-29 17:37:37', '', 0, 'http://step-by-step-ldx1ik2x.hstn.me/?p=145', 0, 'post', '', 0),
