, textarea {\n		-moz-appearance: none;\n		-webkit-appearance: none;\n		-ms-appearance: none;\n		appearance: none;\n	}*/\n\n/* Basic */\n\n	// Set box model to border-box.\n	// Based on css-tricks.com/inheriting-box-sizing-probably-slightly-better-best-practice\n		html {\n			box-sizing: border-box;\n		}\n\n		*, *:before, *:after {\n			box-sizing: inherit;\n		}\n\n	@-ms-viewport {\n		width: device-width;\n	}\n\n	body {\n		font-size: 13.5pt;\n		font-family: sans-serif;\n		background-color: #e3e9dc;\n		font-family: \'Ubuntu Condensed\', sans-serif;\n		color: #878e83;\n		letter-spacing: -1px;\n	}\n\n	h1, h2, h3, h4 {\n		color: #007897;\n		font-weight: normal;\n	}\n\n	h1 {\n		text-transform: lowercase;\n	}\n\n	h2 {\n		text-transform: lowercase;\n		font-size: 1.6em;\n		letter-spacing: -1px;\n		margin-bottom: 1em;\n	}\n\n	h3, h4 {\n		color: #283121;\n		margin-bottom: 0.5em;\n	}\n\n	h3 {\n		font-size: 1.2em;\n		margin-bottom: 0.8em;\n	}\n\n	h4 {\n		font-size: 1em;\n	}\n\n	p, ul {\n		margin-bottom: 1.25em;\n	}\n\n	p {\n		line-height: 1.8em;\n	}\n\n	strong {\n		color: #474e43;\n	}\n\n	img {\n		&amp;.left {\n			float: left;\n			margin: 2px 1.25em 0 0;\n		}\n		&amp;.top {\n			margin: 4px 0 2.25em 0;\n		}\n	}\n\n	ul {\n		padding: 0.25em 0 0 0;\n	}\n\n	a {\n		color: #5d93a2;\n		text-decoration: underline;\n		&amp;:hover {\n			text-decoration: none;\n		}\n	}\n\n	* &gt; p:last-child {\n		margin-bottom: 0 !important;\n	}\n\n	section, article {\n		margin: 0 0 60px 0;\n	}\n\n	section:last-child, article:last-child {\n		margin-bottom: 0;\n	}\n\n/* Container */\n\n	.container {\n		margin: 0 auto;\n		max-width: 100%;\n		width: 1200px;\n\n		@include breakpoint(\'&lt;=xlarge\') {\n			width: 1200px;\n		}\n\n		@include breakpoint(\'&lt;=large\') {\n			width: calc(100% - 70px);\n		}\n\n		@include breakpoint(\'&lt;=medium\') {\n			width: calc(100% - 100px);\n		}\n\n		@include breakpoint(\'&lt;=small\') {\n			width: calc(100% - 40px);\n		}\n	}\n\n/* Row */\n\n	.row {\n		@include html-grid((50px, 50px));\n\n		@include breakpoint(\'&lt;=xlarge\') {\n			@include html-grid((50px, 50px), \'xlarge\');\n		}\n\n		@include breakpoint(\'&lt;=large\') {\n			@include html-grid((35px, 35px), \'large\');\n		}\n\n		@include breakpoint(\'&lt;=medium\') {\n			@include html-grid((50px, 50px), \'medium\');\n		}\n\n		@include breakpoint(\'&lt;=small\') {\n			@include html-grid((20px, 20px), \'small\');\n		}\n	}\n\n/* Reusable */\n\n	article {\n		&amp;.blog-post {\n			position: relative;\n\n			.comments {\n				position: absolute;\n				top: 0;\n				right: 0;\n				height: 32px;\n				line-height: 24px;\n				padding-left: 40px;\n				background: url(\'images/icon-bubble.png\') no-repeat;\n			}\n\n			.blog-post-image {\n				width: 100%;\n			}\n		}\n	}\n\n	ul {\n		&amp;.small-image-list {\n			li {\n				overflow: hidden;\n				margin-bottom: 1.5em;\n\n				img {\n					width: 78px;\n				}\n			}\n		}\n\n		&amp;.big-image-list {\n			li {\n				overflow: hidden;\n				margin-bottom: 2em;\n\n				img {\n					width: 178px;\n				}\n			}\n		}\n\n		&amp;.link-list {\n			li {\n				border-top: solid 1px #ced0b7;\n				padding: 0.75em 0 0 0;\n				margin: 0.75em 0 0 0;\n\n				&amp;:first-child {\n					padding-top: 0;\n					margin-top: 0;\n					border-top: 0;\n				}\n			}\n		}\n	}\n\n	.button {\n		@include vendor(\'background-image\', \'linear-gradient(top, #008dad, #007b9d)\');\n		display: inline-block;\n		padding: 15px 25px 15px 25px;\n		background-color: #007b9d;\n		border-radius: 10px;\n		text-transform: lowercase;\n		text-decoration: none;\n		color: #fff;\n		font-size: 1.2em;\n		letter-spacing: -1px;\n		border: solid 1px #006e8b;\n		box-shadow: inset 0px 0px 0px 1px #18a8c8;\n\n		&amp;:hover {\n			@include vendor(\'background-image\', \'linear-gradient(top, #1b9fbe, #118eb1)\');\n			background-color: #118eb1;\n			box-shadow: inset 0px 0px 0px 1px #3ecceb;\n		}\n	}\n\n/* Header */\n\n	#header-wrapper {\n		@include vendor(\'background-image\', \'linear-gradient(top, #008dab, #007294)\');\n		background-color: #007294;\n	}\n\n	#header {\n		@include vendor(\'background-image\', \'linear-gradient(top, #008ead, #007b9d)\');\n		@include vendor(\'box-sizing\', \'content-box\');\n		position: relative;\n		margin: 2.5% 0 2.5% 0;\n		padding: 0 40px 0 40px;\n		border-radius: 10px;\n		border: solid 1px #006e8b;\n		box-shadow: inset 0px 0px 0px 1px #12a0bf, 0px 1px 4px 0px rgba(0, 0, 0, 0.1);\n		background-color: #007b9d;\n		height: 86px;\n\n		h1 {\n			position: absolute;\n			left: 40px;\n			top: 0;\n			color: #fff;\n			font-size: 2.4em;\n			letter-spacing: -2px;\n			line-height: 86px;\n\n			a {\n				color: #fff;\n				text-decoration: none;\n			}\n		}\n\n		nav {\n			position: absolute;\n			right: 40px;\n			top: 1px;\n			line-height: 84px;\n			text-transform: lowercase;\n			font-size: 1.3em;\n			letter-spacing: -1px;\n\n			a {\n				display: inline-block;\n				text-decoration: none;\n				color: #fff;\n				padding: 0 25px 0 25px;\n				outline: 0;\n\n				&amp;.current-page-item {\n					@include vendor(\'background-image\', \'linear-gradient(top, #007b99, #007897)\');\n					background-color: #007897;\n					box-shadow: inset 0px 1px 5px 1px rgba(0, 0, 0, 0.1), 0px 0px 5px 1px rgba(255, 255, 255, 0.1);\n				}\n\n				&amp;:hover {\n					@include vendor(\'background-image\', \'linear-gradient(top, #0782a0, #077d9b)\');\n					background-color: #0882a1;\n					box-shadow: inset 0px 1px 5px 1px rgba(0, 0, 0, 0.05), 0px 0px 5px 1px rgba(255, 255, 255, 0.05);\n				}\n			}\n		}\n	}\n\n/* Banner */\n\n	#banner-wrapper {\n		background: #c8d2bc url(images/bg1.png);\n		padding: 2em 0;\n	}\n\n	#banner {\n		position: relative;\n		width: 100%;\n		padding: 6em 0;\n		border-radius: 10px;\n		background-image: url(images/banner.jpg);\n		background-size: cover;\n		text-align: center;\n		box-shadow: inset 0 0 0 20px #fff;\n\n		h2 {\n			font-size: 3.3em;\n			color: #fff;\n			text-align: center;\n			letter-spacing: -2px;\n			margin: 0;\n		}\n\n		span {\n			display: block;\n			left: 0;\n			margin: 0.5em 0 0 0;\n			font-size: 1.6em;\n			color: #fff;\n			letter-spacing: -1px;\n			text-transform: lowercase;\n			opacity: 0.8;\n		}\n	}\n\n/* Main */\n\n	#main {\n		background: #fff;\n		padding: 4em 0;\n	}\n\n/* Footer */\n\n	#footer-wrapper {\n		padding: 4em 0;\n	}\n\n/* Copyright */\n\n	#copyright {\n		text-align: center;\n		color: #A6A88F;\n		border-top: solid 1px #ced0b7;\n		padding: 3em 0 0 0;\n		margin: 3em 0 0 0;\n\n		a {\n			color: #A6A88F;\n		}\n	}\n\n/* Large */\n\n	@include breakpoint(\'&lt;=large\') {\n\n		/* Reusable */\n\n			ul {\n				&amp;.small-image-list {\n					img {\n						width: 50px;\n					}\n				}\n\n				&amp;.big-image-list {\n					img {\n						width: 75px;\n					}\n				}\n			}\n\n		/* Header */\n\n			#header {\n				height: 76px;\n\n				h1 {\n					font-size: 2em;\n					line-height: 76px;\n				}\n\n				nav {\n					line-height: 74px;\n					font-size: 1.1em;\n\n					a {\n						padding: 0 20px 0 20px;\n					}\n				}\n			}\n\n		/* Banner */\n\n			#banner {\n				width: 100%;\n				padding: 5em 0;\n\n				h2 {\n					font-size: 3em;\n				}\n\n				span {\n					font-size: 1.4em;\n				}\n			}\n\n		/* Footer */\n\n			#footer-wrapper {\n				padding: 4em 0;\n			}\n\n		/* Copyright */\n\n			#copyright {\n				padding: 3em 0 0 0;\n				margin: 4em 0 0 0;\n			}\n\n	}\n\n/* Small */\n\n	#navPanel, #titleBar {\n		display: none;\n	}\n\n	@include breakpoint(\'&lt;=medium\') {\n\n		/* Basic */\n\n			html, body {\n				overflow-x: hidden;\n			}\n\n		/* Page Wrapper */\n\n			#page-wrapper {\n				padding-top: 44px;\n			}\n\n		/* Header */\n\n			#header-wrapper {\n				@include vendor(\'background-image\', \'linear-gradient(top, #008dab, #007294)\');\n				background-color: #007294;\n			}\n\n			#header {\n				display: none;\n			}\n\n			#banner {\n				padding: 6em 0;\n\n				h2 {\n					font-size: 2.5em;\n				}\n			}\n\n		/* Nav */\n\n			#page-wrapper {\n				@include vendor(\'backface-visibility\', \'hidden\');\n				@include vendor(\'transition\', \'transform #{_duration(navPanel)} ease\');\n				padding-bottom: 1px;\n			}\n\n			#titleBar {\n				@include vendor(\'background-image\', \'linear-gradient(top, #008dab, #007294)\');\n				@include vendor(\'backface-visibility\', \'hidden\');\n				@include vendor(\'transition\', \'transform #{_duration(navPanel)} ease\');\n				display: block;\n				height: 44px;\n				left: 0;\n				position: fixed;\n				top: 0;\n				width: 100%;\n				z-index: _misc(z-index-base) + 1;\n				text-align: center;\n				color: #fff;\n				font-size: 1.25em;\n				background-color: #007294;\n\n				.title {\n					line-height: 44px;\n				}\n\n				.toggle {\n					position: absolute;\n					top: 0;\n					left: 0;\n					width: 80px;\n					height: 60px;\n\n					&amp;:after {\n						content: \'\';\n						position: absolute;\n						left: 4px;\n						top: 4px;\n						color: #fff;\n						text-align: center;\n						line-height: 31px;\n						font-size: 0.8em;\n						width: 50px;\n						height: 35px;\n						border-radius: 5px;\n						box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, 0.25), inset 0px 1px 2px 0px rgba(0, 0, 0, 0.5), inset 0px 6px 13px 0px rgba(255, 255, 255, 0.2), 0px 2px 2px 0px rgba(255, 255, 255, 0.1);\n					}\n\n					&amp;:before {\n						content: \'\';\n						position: absolute;\n						width: 20px;\n						height: 30px;\n						background: url(\'images/mobileUI-site-nav-opener-bg.svg\');\n						top: 16px;\n						left: 19px;\n					}\n\n					&amp;:active {\n						&amp;:after {\n							background: rgba(0, 0, 0, 0.2);\n						}\n					}\n				}\n			}\n\n			#navPanel {\n				@include vendor(\'backface-visibility\', \'hidden\');\n				@include vendor(\'transform\', \'translateX(#{_size(navPanel) * -1})\');\n				@include vendor(\'transition\', (\'transform #{_duration(navPanel)} ease\'));\n				display: block;\n				height: 100%;\n				left: 0;\n				overflow-y: auto;\n				position: fixed;\n				top: 0;\n				width: _size(navPanel);\n				z-index: _misc(z-index-base) + 2;\n				background: #00536F;\n				color: #fff;\n				box-shadow: inset -10px 0px 40px