n				padding: 40px 0;\n			}\n\n		/* Footer */\n\n			#footer-wrapper {\n				padding: 40px 0;\n			}\n\n		/* Copyright */\n\n			#copyright {\n				text-align: center;\n				color: #A6A88F;\n				padding: 30px 0 0 0;\n				font-size: 0.8em;\n				line-height: 1.5em;\n\n				a {\n					color: #A6A88F;\n				}\n			}\n\n	}\n\n	.div-box {\n		padding: 15px 0;\n\n		label {\n			display: block;\n		}\n\n		a {\n			display: block;\n		}\n	}\n\n	.img-block {\n		display: block;\n		max-width: 100%;\n		margin:0 auto;\n	}</pre>\n<!-- /wp:preformatted -->', 'main.scss', '', 'publish', 'open', 'open', '', 'main-scss', '', '', '2023-05-29 17:38:57', '2023-05-29 17:38:57', '', 0, 'http://step-by-step-ldx1ik2x.hstn.me/?p=151', 0, 'post', '', 0);
