(161, 1, '2023-05-29 17:44:06', '2023-05-29 17:44:06', '<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">using Microsoft.AspNetCore.Builder;\nusing Microsoft.AspNetCore.Hosting;\nusing Microsoft.AspNetCore.Identity;\nusing Microsoft.AspNetCore.Mvc;\nusing Microsoft.EntityFrameworkCore;\nusing Microsoft.Extensions.Configuration;\nusing Microsoft.Extensions.DependencyInjection;\nusing Microsoft.Extensions.Hosting;\nusing MyCompany.Domain;\nusing MyCompany.Domain.Repositories.Abstract;\nusing MyCompany.Domain.Repositories.EntityFramework;\nusing MyCompany.Service;\n\nnamespace MyCompany\n{\n    public class Startup\n    {\n        public IConfiguration Configuration { get; }\n        public Startup(IConfiguration configuration) =&gt; Configuration = configuration;\n\n        public void ConfigureServices(IServiceCollection services)\n        {\n            //���������� ������ �� appsetting.json\n            Configuration.Bind(\"Project\", new Config());\n\n            //���������� ������ ���������� ���������� � �������� ��������\n            services.AddTransient&lt;ITextFieldsRepository, EFTextFieldsRepository&gt;();\n            services.AddTransient&lt;IServiceItemsRepository, EFServiceItemsRepository&gt;();\n            services.AddTransient&lt;DataManager&gt;();\n\n            //���������� �������� ��\n            services.AddDbContext&lt;AppDbContext&gt;(x =&gt; x.UseSqlServer(Config.ConnectionString));\n\n            //����������� identity �������\n            services.AddIdentity&lt;IdentityUser, IdentityRole&gt;(opts =&gt;\n            {\n                opts.User.RequireUniqueEmail = true;\n                opts.Password.RequiredLength = 6;\n                opts.Password.RequireNonAlphanumeric = false;\n                opts.Password.RequireLowercase = false;\n                opts.Password.RequireUppercase = false;\n                opts.Password.RequireDigit = false;\n            }).AddEntityFrameworkStores&lt;AppDbContext&gt;().AddDefaultTokenProviders();\n\n            //����������� authentication cookie\n            services.ConfigureApplicationCookie(options =&gt;\n            {\n                options.Cookie.Name = \"myCompanyAuth\";\n                options.Cookie.HttpOnly = true;\n                options.LoginPath = \"/account/login\";\n                options.AccessDeniedPath = \"/account/accessdenied\";\n                options.SlidingExpiration = true;\n            });\n\n            //����������� �������� ����������� ��� Admin area\n            services.AddAuthorization(x =&gt;\n            {\n                x.AddPolicy(\"AdminArea\", policy =&gt; { policy.RequireRole(\"admin\"); });\n            });\n\n            //��������� ������� ��� ������������ � ������������� (MVC)\n            services.AddControllersWithViews(x =&gt;\n                {\n                    x.Conventions.Add(new AdminAreaAuthorization(\"Admin\", \"AdminArea\"));\n                })\n                //���������� ������������� � asp.net core 3.0\n                .SetCompatibilityVersion(CompatibilityVersion.Version_3_0).AddSessionStateTempDataProvider();\n        }\n\n        public void Configure(IApplicationBuilder app, IWebHostEnvironment env)\n        {\n            //!!! ������� ����������� middleware ����� �����\n\n            //� �������� ���������� ��� ����� ������ ����� ������ ������\n            if (env.IsDevelopment()) \n                app.UseDeveloperExceptionPage();\n\n            //���������� ��������� ��������� ������ � ���������� (css, js � �.�.)\n            app.UseStaticFiles();\n\n            //���������� ������� �������������\n            app.UseRouting();\n\n            //���������� �������������� � �����������\n            app.UseCookiePolicy();\n            app.UseAuthentication();\n            app.UseAuthorization();\n\n            //������������� ������ ��� �������� (���������)\n            app.UseEndpoints(endpoints =&gt;\n            {\n                endpoints.MapControllerRoute(\"admin\", \"{area:exists}/{controller=Home}/{action=Index}/{id?}\");\n                endpoints.MapControllerRoute(\"default\", \"{controller=Home}/{action=Index}/{id?}\");\n            });\n        }\n    }\n}</pre>\n<!-- /wp:preformatted -->', 'Startup.cs', '', 'publish', 'open', 'open', '', 'startup-cs', '', '', '2023-05-29 17:44:07', '2023-05-29 17:44:07', '', 0, 'http://step-by-step-ldx1ik2x.hstn.me/?p=161', 0, 'post', '', 0),
